drop table if exists Recensione;
drop table if exists Videogioco;
drop table if exists Commento;
drop table if exists Utente;

CREATE TABLE Videogioco(
    titolo VARCHAR(100) PRIMARY KEY,
    rilascio DATE NOT NULL,
    casaProduttrice VARCHAR(50) NOT NULL,
    imgLocandina VARCHAR(100) NOT NULL,
    imgBanner VARCHAR(100),
    trama TEXT NOT NULL,
    piattaformaV VARCHAR(100) NOT NULL,
    genereV VARCHAR(100) NOT NULL
);

CREATE TABLE Recensione(
    idRecensione VARCHAR(30) PRIMARY KEY,
    voto INTEGER NOT NULL,
    contenuto TEXT NOT NULL,
    titolo VARCHAR(200) NOT NULL,
    dataPubblicazione DATE NOT NULL,
    idVideogioco VARCHAR(100) NOT NULL,
    FOREIGN KEY(idVideogioco) REFERENCES Videogioco(titolo) on update cascade on delete cascade
);

CREATE TABLE Utente(
    email VARCHAR(100) PRIMARY KEY,
    nickname VARCHAR(30) UNIQUE NOT NULL,
    password VARCHAR(30) NOT NULL,
    dataIscrizione DATE NOT NULL,
    imgProfilo VARCHAR(100)
);

CREATE TABLE Commento(
    idCommento VARCHAR(30) PRIMARY KEY,
    data DATE NOT NULL,
    voto INTEGER NOT NULL,
    contenuto VARCHAR(900) NOT NULL,
    idUtente VARCHAR(100) NOT NULL,
    idVideogioco VARCHAR(100) NOT NULL,
    FOREIGN KEY(idUtente) REFERENCES Utente(email) on update cascade on delete cascade,
    FOREIGN KEY(idVideogioco) REFERENCES Videogioco(titolo) on update cascade on delete cascade
);

INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Assassin's Creed 2","2009-11-19","Ubisoft","imgs/locandina Assassin's Creed 2.jpeg","","In seguito agli eventi del primo capitolo, Desmond Miles viene salvato dall'Abstergo dall'assassina sotto copertura Lucy Stillman e viene portato in un rifugio dove incontra la sua squadra, composta dallo storico e analista Shaun Hastings e dal tecnico Rebecca Crane. Questi hanno costruito un proprio Animus, che intendono usare per addestrare Desmond come assassino tramite il cosiddetto 'effetto osmosi', grazie al quale potrà assimilare rapidamente le abilità del proprio antenato. Desmond rivive quindi i ricordi di Ezio Auditore da Firenze, nato in una nobile famiglia fiorentina nel 1459, in pieno Rinascimento italiano. Nel 1476, Ezio è divenuto un giovane carismatico ma spericolato. Quando suo padre Giovanni e i suoi due fratelli Federico e Petruccio vengono accusati di tradimento e giustiziati in piazza dal corrotto gonfaloniere Uberto Alberti, Ezio recupera l'equipaggiamento da assassino del padre e si vendica uccidendo Uberto. Ezio e ciò che resta della sua famiglia, sua madre Maria e sua sorella Claudia, fuggono da Firenze per dirigersi nella loro casa natale a Monteriggioni. Lì lo zio di Ezio, Mario, gli racconta del ruolo della sua famiglia come membri della Confraternita degli Assassini e lo addestra come assassino. Nei dieci anni successivi, Ezio bracca gli uomini responsabili della congiura ai danni della sua famiglia, tutti appartenenti alle famiglie dei Pazzi o dei Barbarigo e membri dell'Ordine templare, nemico giurato degli assassini. Nel corso del suo viaggio Ezio conosce anche numerosi alleati, come Lorenzo de' Medici, lo statista che salva dalla Congiura dei Pazzi, l'inventore Leonardo da Vinci, che lo rifornisce di nuovo equipaggiamento basandosi sulle informazioni contenute nel Codice lasciato dal leggendario assassino Altaïr Ibn-La'Ahad, e infine Caterina Sforza, la contessa di Forlì. Ezio scopre in seguito l'identità del Gran maestro templare Rodrigo Borgia, che trama di impossessarsi dell'Italia unificando sotto la sua leadership le più influenti famiglie della penisola. Nel 1488, Ezio si traveste da soldato incaricato di trasportare una Mela dell'Eden e affronta Rodrigo, che gli rivela di voler aprire la 'Cripta', un sotterraneo segreto all'interno del quale è convinto si trovi Dio in persona. Rodrigo quasi uccide Ezio, ma è costretto a fuggire abbandonando la mela in seguito all'intervento degli alleati di Ezio. Come ricompensa per il suo aiuto, Mario integra ufficialmente Ezio nella confraternita. Nel presente, Desmond soffre i danni collaterali dell'effetto osmosi, sperimentando visioni di Altaïr. Dentro l'Animus, trova inoltre dei glifi simili a quelli lasciati nella sua cella all'Abstergo; una volta decifrati, questi mostrano due schiavi intenti a rubare una mela. Il video termina con dei caratteri ASCII che formano la parola 'EDEN', portando la squadra a credere che i due del video siano Adamo ed Eva, i primi esseri umani creati da Dio. Con numerose sequenze genetiche troppo corrotte per essere accessibili (poi rese giocabili come DLC e rese parte integrante della storia nella versione PS4), la squadra manda Desmond a rivivere l'ultimo ricordo, risalente al 1499. Dopo aver scoperto che la Cripta si nasconde sotto il Palazzo Papale, Ezio si infiltra nel Vaticano e affronta Rodrigo, ora Papa Alessandro VI, che combatte brandendo il Bastone papale, in realtà un altro Pezzo dell'Eden. Ezio sconfigge Rodrigo ma lo risparmia, avendo ormai superato la propria sete di vendetta. Utilizzando la Mela e il Bastone, Ezio sblocca dunque la Cripta e viene contattato da un ologramma di Minerva. Sapendo che Desmond sta ascoltando, Minerva spiega che il suo popolo, la Prima Civiltà, ha creato gli esseri umani perché li servissero, ma è stato spazzato via da un cataclisma. I sopravvissuti hanno scelto di collaborare con l'umanità, costruendo una serie di cripte per preservare la propria tecnologia ed evitare che un simile disastro possa ripetersi. Prima di svanire, dice a Desmond che solo lui ha il potere di adempiere alla 'profezia', lasciando confusi sia lui che Ezio. Nel presente, alcuni agenti dell'Abstergo guidati da Warren Vidic attaccano il nascondiglio, obbligando la squadra ad evacuare. Durante la fuga, Lucy rivela a Desmond che gli assassini hanno rivelato alcuni strane anomalie nel campo geomagnetico: tra qualche mese avrà luogo un'eruzione solare, probabilmente lo stesso cataclisma che ha sterminato la Prima Civiltà. Desmond si prepara quindi a rientrare nell'Animus.","PlayStation 3 | PlayStation 4 | XBox 360 | XBox One | Nintendo Switch | PC","Avventura | Stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Assassin's Creed 4 Black Flag","2013-11-29","Ubisoft","imgs/locandina Assassin's Creed Black Flag.jpg","","Edward James Kenway è un ex corsaro della marina britannica che ha deciso di trasferirsi nei Caraibi in cerca di fortuna e avventura, abbandonando la moglie Caroline Scott in Galles e promettendole che sarebbe tornato dopo 2 anni. Nell'estate del 1715, Edward guida il brigantino pirata Jacobite in uno scontro a fuoco contro l'HMS Intrigue, una fregata inglese. L’uomo abbatte la flotta nemica, ma naufraga sulla spiaggia di Capo Bonavista quando la polveriera della Jacobite esplode. L'individuo che guidava la flotta nemica gli chiede di farlo arrivare all'Avana, cercando di corromperlo, per poi puntare una pistola contro Edward e fuggire nella giungla, venendo successivamente raggiunto e ucciso da Edward stesso, il quale gli ruba lettera da parte del governatore delle Bermuda, Laureano Torres y Alala. Gli ruba anche gli abiti poiché nella lettera sopra citata vi era scritto che Torres non lo ha mai incontrato. Scoprendo quindi nella lettera che l'uomo che ha ucciso è Duncan Walpole e che ha tradito gli Assassini. Edward fa subito amicizia col mercante inglese Stede Bonnet, il quale, dopo essere stato salvato da Edward stesso da alcuni soldati inglesi, che lo avevano scambiato per un pirata, gli offre la sua nave per partire assieme alla volta dell'Avana. Arrivati nella capitale di Cuba, Edward raggiunge Torres e gli altri Templari, aiutandoli a interrogare un uomo chiamato 'il Saggio', per poi scoprire la posizione dell'Osservatorio: luogo costruito dai Precursori e dal quale è possibile spiare chiunque con una goccia di sangue di un Saggio. Edward strappa l’uomo dalle grinfie degli Assassini, anche se quest’ultimo non sembra interessato a collaborare con i membri di nessuna delle due fazioni. Dopo aver recuperato il Saggio, Edward riceve 1.000 real come ricompensa; tuttavia, rimasto insoddisfatto, Edward va a cercare Roberts da solo e si addentra nella piantagione per poi venire scoperto e ferito da El Tiburón, la guardia personale del corpo di Torres. Nell'anno 2013, un impiegato dell'Abstergo Entertainment conosce l'impiegata Melanie Lemay e il capo Olivier Garneau, facendo anche amicizia con il corriere Rebecca e con il barista Shaun, visti nei precedenti capitoli. In seguito, viene convocato da Olivier Garneu nel suo ufficio; l'uomo chiede all'impiegato di concentrare le sue ricerche sull'Osservatorio, come gli è stato chiesto di riferirgli dai dirigenti di Abstergo Industries. Subito dopo, viene contattato da John, il quale gli chiede, con una scusa, di violare l'Animus di un collega e di consegnare i file ottenuti ad un corriere presente nell'atrio. Dopo averlo fatto, l'impiegato rientra nell'Animus. Venendo catturato e deportato sul vascello principale di una flotta spagnola, Edward raduna una ciurma grazie all'aiuto dell'ex schiavo nato a Trinidad, Adéwalé, con cui in seguito instaura un rapporto di amicizia. Dopo essersi impossessato di una nave, che chiamerà Jackdaw, Edward viene a conoscenza dell'esistenza dell'Ordine degli Assassini grazie all'amico James Kidd, collega di Kenway, Edward Tatch (Barbanera), Benjamin Hornigold, 'Calico' Jack Rackham e Charles Vane. Dopo essere venuto a conoscenza dell'Ordine degli Assassini, Edward si reca a Tulum (Yucatán, Messico), dove conosce Ah Tabai e dove libera il posto dai soldati inglesi, venendo successivamente assolto dal mentore per aver ucciso degli Assassini all'Avana. Edward inizia a dare la caccia agli agenti templari di Torres, cominciando da Julien DuCasse, un templare francese assassinato a Great Inagua. L’uomo procede la propria missione cercando il nuovo governatore, Woodes Rogers, il quale ha deciso di concedere ai pirati catturati la scelta di chiedere perdono e tornare in patria in uno stato di povertà, al posto di finire appesi alla forca. Nel frattempo, Edward assassina il commodoro Chamberlane, cura gli abitanti di Nassau con delle medicine reperite dai soldati spagnoli a Charles Town e sotto i fondali marini, poi viene tradito da Rakham, che cerca di vendere Adéwalé agli schiavisti di Kingston per poi abbandonare Edward con Vane, abbattuto nell'Isla Providencia. Infine, Edward uccide lo schiavista olandese, Lawrence Prince. Nel frattempo, nel 2013, John chiede un altro favore al nuovo impiegato di Abstergo Entertainment: violare la telecamera di sicurezza dell'ufficio di Olivier per spiare un incontro tra l'uomo, Melanie e Laetitia England. L'impiegato, minacciato dal tecnico dell'IT di denunciare le sue attività illecite della settimana appena trascorsa, viola il computer di Olivier e ruba dei file riservati, scoprendo che l'Abstergo è la società di facciata dell'odierno Ordine dei Templari. Dopo la scoperta, l'impiegato ritorna nell'Animus. Essendo venuto a conoscenza che Barbanera ha intenzione di attaccare un vascello inglese da solo, Edward corre in suo aiuto, ma non riesce ad evitare la morte dell'amico, ucciso da un soldato inglese durante un abbordaggio. Edward si allea con Roberts a Príncipe (Africa), liberando il territorio dai soldati portoghesi e assassinando Hornigold, unitosi ai Templari. Dopo aver trovato l'Osservatorio, Edward ne contempla il funzionamento e viene tradito da Roberts, che lo fa deportare nella prigione di Kingston; tuttavia, Edward riesce a fuggire con l'aiuto di Ah Tabai, ma Mary Read, la vera James Kidd, muore. Adéwalé si unisce agli Assassini ed Edward sceglie Anne Bonny come nuovo quartiermastro. Alla fine, Edward riesce a uccidere Rogers a Kingston, spacciandosi per un diplomatico italiano e, successivamente, insegue e abborda la nave di Roberts, che uccide nelle coste di Príncipe. Nel 2013, l'impiegato è stato inconsapevolmente trasferito in un bunker sotterraneo, dove verrà tenuto prigioniero fino a che non si riuscirà a comprendere chi sia il responsabile delle violazioni nei sistemi di sicurezza nelle settimane precedenti. Facendo finta di volerlo aiutare a cancellare le prove che potrebbero portare i dirigenti a capire chi è il colpevole, John tenta, invano, di fare in modo che Giunone, vista nei precedenti capitoli, prenda possesso del corpo dell'impiegato che, scosso dalla vicenda, torna successivamente nell'Animus. Edward progetta di uccidere Torres e si reca all'Avana, al momento messa in coprifuoco a causa sua, dove chiede all'amica Rhona Dismore di tenere il teschio e il cubo per attivare l'Osservatorio. Edward segue poi El Tiburón fino alla fortezza spagnola locale, per assassinare Torres; tuttavia, Edward riesce a uccidere solo il sosia del Templare, ma ne approfitta anche per assassinare la sua guardia del corpo. A questo punto, Edward rintraccia e va a prendere Torres, uccidendolo all'Osservatorio. Nel 2013, John fa uscire l'impiegato dall'Animus e si mostra per la prima volta: col volto identico al pirata Bartholomew Roberts, John rivela di essere uno dei Saggi ed anche marito di Giunone. Immediatamente dopo, l’uomo inietta un liquido nel corpo dell'impiegato, per indebolirlo abbastanza da permettere alla moglie di prendere possesso del suo corpo; tuttavia, l'impiegato viene salvato dalle guardie di sicurezza, che uccidono John dopo aver fatto irruzione nel bunker sotterraneo. Assistito dai medici dell'Abstergo, l'impiegato viene reintegrato nel suo posto di lavoro e ringraziato per il suo aiuto involontario da Shaun e Rebecca, membri dell'Ordine degli Assassini, che gli chiedono di continuare a collaborare con loro. L'impiegato decide allora di tornare nell'Animus. Edward si reca a Great Inagua e vede la figlia avuta con Caroline Scott per la prima volta, decidendo poi di andare a vivere in Inghilterra con lei.","PlayStation 3 | PlayStation 4 | XBox 360 | XBox One | PC","Avventura | Stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Assassin's Creed Odyssey","2018-10-05","Ubisoft","imgs/locandina Assassin's Creed Odyssey.jpg","","Mesi dopo la sua avventura in Egitto, Layla Hassan è ormai un'agente degli Assassini; durante una campagna di ricerche, rinviene la lancia appartenuta al re di Sparta Leonida, spezzatasi dopo la battaglia delle Termopili, e vi riconosce un manufatto della Prima Civilizzazione; con l'aiuto di Victoria Bibeau, Layla estrae il DNA di due fratelli vissuti nel V secolo a.C., Alexios e Kassandra (segue la scelta del giocatore con quale dei due fratelli giocare). Si inizia così a seguire la storia del protagonista scelto attraverso l'Animus, con lo scopo di scoprire l'ubicazione del Bastone di Ermete Trismegisto. Il protagonista sarà conosciuto come (il/la mercenario/a).
Il Misthios è il primogenito di Nicolao e Myrrine, rispettivamente un guerriero spartano e la figlia di Leonida. Durante l'infanzia del Misthios, Nicolao apprende una profezia, secondo la quale il figlio minore (Alexios o Kassandra a seconda della scelta del giocatore) avrebbe portato distruzione in tutta la Grecia. Senza opporsi troppo, viene quindi costretto dai sacerdoti spartani a gettarlo dal monte Taigeto. Nel tentativo di salvare il fratello neonato, il piccolo 'Misthios' provoca la caduta dell'oracolo che aveva predetto la profezia, con quest'ultimo che regge tra le mani lo Alexios/Kassandra. Dopo questo omicidio, a Nicolao viene ordinato di sacrificare anche il proprio primogenito che, nonostante lo sconforto del padre, viene lanciato dalla rupe. Ciascuno all'insaputa dell'altro, i due fratelli sopravvivono alla caduta; il Misthios, recuperando la lancia spezzata di Leonida, raggiunge Cefalonia e viene trovato da un avido mercante di nome Marco, che lo adotta e lo cresce come abile mercenario. Il Misthios cresce insieme alla piccola orfana chiamata Febe, compiendo lavori sporchi fino allo scoppio della Guerra del Peloponneso.
Un giorno, il Misthios viene contattato da Elpenore, un misterioso individuo che le offre un incarico da assassino: l'uccisione del 'Lupo di Sparta'. Lei accetta, ma nel corso della missione scopre che il Lupo non è altri che suo padre Nicolao, ora generale e padre di un figlio adottivo, Stentore, anch'egli soldato spartano. Il Misthios aiuta Stentore a conquistare la Megaride, in modo di ottenere un colloquio con il Lupo. Raggiunta la vittoria sugli ateniesi in quella regione, il Misthios e Nicolao si confrontano animatamente. Nonostante non veda Nicolao da molto tempo, il Misthios si scaglia duramente e brutalmente contro di lui, accusandolo di non aver fatto abbastanza per proteggere i suoi figli dalla profezia che, a parere suo ma anche della madre Myrrine, era ingiusta. Al termine del suo sfogo nei confronti del padre, il Misthios può decidere se uccidere o meno Nicolao. Qualsiasi sia la scelta del giocatore, Nicolao rivela di non essere il loro padre genetico, ma di aver cresciuto e amato i due bambini come fossero suoi, nonostante il suo dovere da spartano lo avesse indotto a sacrificarli entrambi. Il generale rivela quindi che Myrrine aveva concepito i due fratelli con un altro uomo, esortando il Misthios a ricercare la madre e conoscere la verità sulle sue origini.
Una volta tornato da Elpenore, quest'ultimo gli rivela di aver sempre saputo che Nicolao era il suo patrigno, e le affida un'altra missione: uccidere Myrrine. Il mercenario rifiuta ed Elpenore fugge. Il Misthios si reca quindi a Delfi per chiedere alla Pizia dove si trovi Myrrine; qui incontra Erodoto, che riconosce nell'arma che il mercenario porta con sé la Lancia di Leonida, e decide di aiutarlo nelle sue missioni. La Pizia parla al Misthios di una misteriosa fratellanza, la Setta di Cosmos, che ha commissionato l'eliminazione della sua intera famiglia; il Misthios rintraccia e uccide dunque Elpenore, membro anch'egli dell'organizzazione, utilizzando il suo travestimento per introdursi di nascosto a una riunione della Setta. Qui scopre che la Setta di Cosmos è un potente ordine di guerrieri e politici, provenienti da ogni angolo della Grecia, il cui scopo è prenderne possesso tramite la guerra del Peloponneso, fomentando di proposito il caos tra la popolazione per ergersi poi a salvatori del mondo greco. Nella stessa riunione incontra il fratello minore Alexios/Kassandra, ora Deimos, allevato dalla Setta e divenuto un guerriero semi-divino e senza scrupoli al servizio dell'organizzazione.
Il Misthios intraprende dunque un viaggio per tutta la Grecia, eliminando i componenti della Setta e ripulendo il mondo dalla loro corruzione; di tutti questi membri, è possibile lasciare in vita solo Lago (questo in realtà era stato semplicemente schiavizzato dalla Setta, e avendo una famiglia a rivolerlo con sé, se sopravvive può aiutare il Misthios nella sua ricerca). Durante il suo viaggio combatte di volta in volta per Sparta o per Atene, alleandosi e stringendo amicizie con figure come Pericle, Aspasia, Ippocrate, Socrate, Alcibiade, Aristofane, Brasida e Fidia. Dopo aver avvertito Pericle della minaccia rappresentata dalla Setta di Cosmos, il Misthios si ritrova in un'Atene messa in ginocchio dalla peste, evento che favorisce la stessa Setta nel raggiungere l'indebolito padre della democrazia, uccidendolo per mano di Deimos. Anche Febe, che aveva raggiunto Atene poco prima, viene uccisa dai sicari della Setta di Cosmos, costringendo il Misthios ad abbandonare la polis ateniese insieme ad Aspasia, compagna di Pericle e successivo bersaglio dei cospiratori. Dopo questi eventi, il Misthios riesce a ricongiungersi con Myrrine, scappata da Sparta dopo la tragica notte della presunta morte dei suoi due figli e ora a capo dell'isola di Nasso dopo aver intrapreso, per breve tempo, la vita da pirata. Il Misthios aiuta la ritrovata madre ad eliminare la minaccia di Paro, l'isola rivale di Nasso, prima di tornare a Sparta per riappropriarsi della propria casa. Myrrine spiega che il vero padre di entrambi i fratelli è Pitagora, appartenente come lei alla stirpe degli Isu, i discendenti della Prima Civilizzazione, dotati di forza sovrumana e del 'senso dell'aquila', cosa che aveva indotto la Setta a plasmare Deimos in un'arma umana con cui rintracciare e uccidere gli altri membri della famiglia. Lei e Pitagora concepirono i due figli per preservare la linea genealogica di Leonida e di Coloro che vennero prima.
Tornate a Sparta, il Misthios smaschera uno dei due re, Pausania, come membro della Setta e lo uccide. Successivamente, durante una battaglia tra spartani e ateniesi a Pilo, giunge allo scontro con Deimos, al termine del quale viene catturato dalla Setta. Durante la permanenza nella prigione di Atene, il protagonista scopre che anche Cleone, leader di Atene dopo la morte di Pericle, è un membro dell'organizzazione nemica. Il Misthios evade di prigione e, grazie all'aiuto di Socrate, Aristofane, Alcibiade e Brasida (vecchio generale spartano e amico della famiglia del Misthios), riesce prima ad uccidere gli ultimi membri della Setta rimasti prima di Cleone, per poi smascherare la corruzione di quest'ultimo e subito dopo eliminarlo sul campo di battaglia nei pressi di Anfipoli. Nello stesso scontro, Brasida viene ucciso da Deimos che, durante il confronto con il misthios, viene ferito gravemente alla schiena da una freccia. Il Misthios e Myrrine, pensando che Deimos sia morto, si recano nuovamente sul monte Taigeto per mettersi una volta per tutte il loro tragico passato alle spalle. Qui ritrovano Deimos, che Myrrine continua a chiamare Alexios/Kassandra, sopravvissuto alla battaglia e ad aspettarli sul monte. Qui madre e figlio/a tentano in ogni modo di riportare il secondogenito alla ragione, convincendolo che la Setta lo ha sempre usato e che la stessa organizzazione convinse l'oracolo di comunicare la falsa profezia. La scelta del giocatore può portare al rinsavimento di Deimos, che si ricongiunge al resto della famiglia, oppure al fallimento dell'iniziativa, con conseguente scontro tra i due fratelli e con il Misthios che uccide il fratello minore. La scena finale dipende dalle scelte del giocatore ed il Misthios può ritrovarsi a cenare con tutti i familiari rimasti in vita in base alle scelte fatte. Quindi si potrà ritrovare nella sua casa natale con Myrrine, Deimos (se persuaso), Nicolao( se risparmiato) e Stentore (se non si è ucciso Nicolao e poi lo si risparmia). I familiari supersisti quindi si ritrovano tutti riuniti a cenare nella casa natale dell'intera famiglia. Felici più che mai iniziano una nuova vita, tutti insieme.
Il Misthios, inoltre, incontra il suo vero padre Pitagora, mantenuto in vita dal Bastone di Ermete Trismegisto e posto ora a guardia di Atlantide, l'ultima roccaforte della Prima Civilizzazione. Il matematico chiede al Misthios di aiutarlo a sigillare la città perduta, con lo scopo di preservare per sempre la saggezza conservatavi; per farlo, il mercenario dovrà sconfiggere quattro creature mitologiche: la Sfinge, il Minotauro, il Ciclope e Medusa, in realtà ologrammi creati dalla Prima Civilizzazione per chiudere i cancelli di Atlantide.
Una volta eliminati tutti gli adepti della Setta di Cosmos, il Misthios si reca nel loro luogo d'incontro per distruggere la Piramide, un artefatto che permetteva loro di prevedere il futuro e che usavano per soggiogare la Grecia. Nel distruggerla, la Piramide conferisce al Misthios una visione delle battaglie che avverranno (corrispondenti alla timeline dell'intera serie) e del mondo spaccato a metà tra l'eterna lotta tra Ordine e Caos. Poco dopo, il capo della Setta si mostra al Misthios: si tratta di Aspasia, che rivela di essere stata un tempo al vertice della Setta, ma di essersi tirata indietro quando essa era diventata man mano più corrotta e crudele al punto di uccidere il suo amato Pericle, e pertanto lo ringrazia per averla distrutta, volendo ora creare una nuova visione utopica in cui l'ordine avrebbe prevalso su tutto. Il Misthios può decidere se lasciarla in vita, sapendo che in fondo non sia lei la diretta responsabile dei disastri della Confraternita, o se ucciderla per mettere fine una volta per tutte alla Setta.
Una volta trovate le Chiavi di Atlantide, il Misthios torna da Pitagora per sigillare la città; con questa azione si attiva una registrazione di Aletheia, una dei Precursori, che avverte il Misthios (quindi anche Layla) che la loro saggezza non è fatta per il genere umano, e che andrà distrutta per permettere agli uomini di sviluppare il loro vero potenziale. Pitagora cede il Bastone al Misthios in modo consenziente, per poi morire.
Nel presente, Layla utilizza le informazioni raccolte tramite la storia del Misthios per localizzare Atlantide e vi si reca per riaprire la città; qui, con grande sorpresa, ritrova il Misthios, tenuto in vita dal Bastone di Ermete. Questi rivela a Layla che il mondo necessita di un equilibrio continuo tra ordine (i Templari) e il caos (gli Assassini), e che il prevalere di qualsiasi delle due fazioni sull'altra causerebbe la distruzione dell'umanità; quando Layla rivela di essere un'Assassina che un tempo era una Templare, il Misthios le rivela che potrebbe essere lei la chiave per l'equilibrio tra ordine e caos, come era stato previsto nella profezia della Piramide. Il Misthios si sacrifica consegnando il Bastone di Ermete a Layla, non prima di averle chiesto di proseguire la sua battaglia e di distruggere tutti i manufatti della Prima Civilizzazione","PlayStation 4 | XBox One | PC","Azione | Stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Assassin's Creed Origins","2017-10-27","Ubisoft","imgs/locandina Assassin's Creed Origins.jpg","","Nel presente, Layla Hassan, ricercatrice dell'Abstergo, ha messo a punto una versione modificata dell'Animus, che consente a chi la usa di non necessitare dello stesso corredo genetico del soggetto di cui rivive i ricordi; contravvenendo agli ordini di Sophia Rikkin, la donna si reca in Egitto dove, invece di rintracciare un artefatto della Prima Civilizzazione, localizza la tomba di due Assassini e, coadiuvata in remoto dall'amica e collega Deanna, sperimenta il macchinario, rivivendo le loro memorie.
La donna scopre così la storia di Bayek di Siwa e sua moglie Aya, due coniugi egiziani, entrambi guerrieri, vissuti nel I secolo a.C. Bayek, uno degli ultimi medjay rimasti, protegge la prospera oasi di Siwa; un giorno, nel 49 a.C., lui e suo figlio Khemu vengono rapiti da un gruppo di cinque uomini mascherati, che cercano di costringere l'uomo ad aprire una cripta segreta, nascosta sotto il tempio di Amon a Siwa. Al rifiuto di Bayek segue una colluttazione, durante la quale il piccolo Khemu viene accidentalmente ucciso. Bayek ed Aya giurano vendetta contro i misteriosi uomini e si separano per cercarli ed eliminarli.
Un anno dopo, Bayek è riuscito a rintracciare e uccidere un membro del gruppo dei mascherati, Rudjek, conosciuto come l'Airone; tornato a Siwa, scopre che il sacerdote del tempio Medunamun è, in realtà, un altro adepto, l'Ibis. Dopo averlo assassinato, Bayek si reca ad Alessandria, dove ritrova sua moglie Aya, la quale si è alleata con Cleopatra e, grazie al suo supporto, ha rintracciato e ucciso altri due membri del gruppo, l'Avvoltoio e l'Ariete. Rimane un unico membro in vita, conosciuto come il Serpente; i due riescono a scoprirne l'identità segreta: si tratta di Eudoro, uno scriba greco. Bayek lo rintraccia nelle terme di Alessandria e lo uccide con un'arma consegnatagli da Aya, la prima lama celata, al costo del suo anulare, che viene amputato durante la colluttazione (da qui il rito di tagliarsi l'anulare, come farà Altaïr, per usare la lama). Prima di morire, tuttavia, Eudoro instilla in Bayek il dubbio di non essere l'ultimo membro del gruppo degli uomini misteriosi.
Bayek incontra così Cleopatra, la quale gli rivela che, effettivamente, Eudoro aveva come nome di battaglia: l'Ippopotamo; il Serpente è, in realtà, il nome collettivo di una società segreta conosciuta come L'Ordine degli Antichi, i cui membri hanno preso possesso dell'intero Egitto e l'hanno spodestata utilizzando il nuovo faraone, suo fratello Tolomeo XIII, come burattino per i propri malvagi scopi. I cinque uomini uccisi da Bayek ed Aya erano, in realtà, gli adepti di rango più basso, mentre altri quattro di stirpe più elevata controllano altrettante zone del Regno, tenendo in scacco la popolazione con metodi brutali e terrificanti. Bayek accetta dunque di diventare il medjay di Cleopatra e viaggia attraverso tutto l'Egitto, aiutando il popolo oppresso e ottenendo informazioni sull'identità dei quattro adepti, conosciuti come lo Scarabeo, la Iena, il Coccodrillo e la Lucertola. Il guerriero riuscirà a individuarli e assassinarli tutti, liberando di volta in volta le regioni che essi controllavano. Aya, nel frattempo, si reca in Grecia per convincere Gneo Pompeo Magno ad allearsi con Cleopatra.
In seguito, Bayek e Aya ricevono la notizia che nessuno di coloro che hanno ucciso è stato, in realtà, colpevole della morte di Khemu: ci sono, infatti, altri membri dell'Ordine, di grado ancora superiore, infiltrati nelle file della guardia personale di Tolomeo: lo Scorpione e lo Sciacallo. Bayek scopre che quest'ultimo è, in realtà, Lucio Settimio, capo dei Gabiniani, ma non riesce a impedirgli di uccidere Pompeo. Non avendo più alleati, Cleopatra decide di incontrarsi direttamente con Giulio Cesare e, con l'aiuto di Bayek ed Aya, riesce a introdursi furtivamente nella sua corte avvolta in un tappeto. Cesare, sedotto da Cleopatra, diventa suo alleato e volta le spalle a Tolomeo: scoppia così la guerra civile alessandrina. Durante gli scontri, Bayek ed Aya aiutano attivamente Cesare e Cleopatra; il medjay riesce a rintracciare lo Scorpione, sotto la cui maschera si nasconde il consigliere Potino, sventa i suoi piani e lo uccide. Tuttavia, Cesare gli impedisce di assassinare anche Settimio. Aya, invece, rinuncia ad uccidere Tolomeo che, tuttavia, muore divorato da un coccodrillo mentre tenta di scappare navigando sul Nilo.
Una volta terminata la guerra, Cleopatra riprende il trono e diventa il nuovo faraone d'Egitto; tuttavia, lei e Cesare, ora legati anche sentimentalmente, tagliano i rapporti con Bayek ed Aya e prendono Settimio come loro consigliere. Ciò fa capire ai due che i sovrani si sono a loro volta alleati con gli Antichi. I due medjay decidono quindi di reclutare le persone che hanno aiutato durante le loro missioni per formare la Confraternita degli Occulti, un nuovo Ordine che difenda il popolo e il suo libero arbitrio. Bayek, intanto, si rende conto che l'Ordine degli Antichi aveva messo gli occhi sulla tomba di Alessandro Magno e insieme ad Aya vi si reca, ma solo per trovare Apollodoro ferito a morte, ucciso dal luogotenente di Cesare, Flavio. Questi è, in realtà, il Leone, capo dell'Ordine degli Antichi e colpevole materiale della morte di Khemu. Flavio si è impossessato dello Scettro e del Globo contenuti nella tomba, in realtà artefatti della Prima Civilizzazione; grazie ad essi, riesce a penetrare nella cripta di Siwa e a capirne i segreti. Bayek e Aya vi si recano, ma troppo tardi: Flavio ha massacrato tutti i medjay che la difendevano.
Bayek, ufficialmente rimasto l'ultimo medjay ancora in vita, si reca allora a Cirene, dove finalmente riesce ad affrontare Flavio, reso più forte dai poteri della Mela dell'Eden, e lo sconfigge, vendicando definitivamente la morte di Khemu e di tutte le persone uccise dall'Ordine. Il medjay torna da Aya che, nel frattempo, si è alleata con Marco Giunio Bruto e Gaio Cassio Longino per sconfiggere Cesare. Alla partenza della donna per Roma, i due coniugi capiscono che il loro amore è stato sacrificato in nome della missione degli Occulti. I due si dicono addio, giurando però di ritrovarsi nella morte e di continuare a proteggere il mondo dalla malvagità. Bayek lascia cadere un amuleto appartenuto a Khemu, il teschio di un'aquila, che imprime nella sabbia, un disegno che diventerà poi il simbolo degli Assassini, lasciando intendere che gli Occulti siano gli antenati di quest'Ordine.
Aya si reca a Roma, dove affronta Settimio e poi assassina Cesare; successivamente, si recherà da Cleopatra per ucciderla, ma vedendo il piccolo Cesarione, si impietosisce e la risparmia, ammonendola però di essere una buona sovrana, altrimenti tornerà per finire il lavoro. Aya cambia nome in Amunet e si stabilisce a Roma, dove fonda una sezione degli Occulti; anche Bayek, in Egitto, continua la sua missione, reclutando nuovi adepti e difendendo gli oppressi.
Nel presente, Layla viene rintracciata e attaccata dai membri dell'Abstergo, che hanno notato la sua assenza; grazie alle abilità ottenute con l'effetto osmosi, Layla è in grado di difendersi, ma Deanna viene catturata e uccisa. Layla giura vendetta e rientra nell'Animus, decisa a portare a termine la sua missione; al termine dell'impresa, viene raggiunta da William Miles, padre di Desmond, che le offre protezione in cambio di aiuto agli Assassini. Layla accetta di collaborare, ma non di entrare a far parte dell'Ordine e i due partono insieme per Alessandria.","PlayStation 4 | XBox One | PC","Azione | Stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Far Cry 3","2012-11-29","Ubisoft","imgs/locandina Farcry 3.webp","","Il gioco è ambientato interamente nel vastissimo arcipelago di Rook Islands presumibilmente in Indonesia, da anni sconvolto da una guerra civile tra le tribù indigene e i mercenari guidati dal signore della guerra Hoyt Volker e i sanguinari pirati dal folle Vaas Montenegro, suo collaboratore gestente l'isola a nord. Il protagonista giocabile è Jason Brody, uno studente di Los Angeles, paracadutatosi con la fidanzata Liza, i fratelli Grant e Riley e gli amici Daisy, Oliver e Keith, durante un viaggio a Bangkok, e finiti sfortunatamente sull'arcipelago e catturati dai criminali. Dopo un primo incontro con il capo dei pirati che minaccia di liberarli solo dietro riscatto, Jason e Grant fuggono. Quest'ultimo grazie all'addestramento militare riesce a fuggire e a portare in salvo anche Jason, ma a un passo dalla salvezza, Grant viene ucciso dallo spietato Vaas con un colpo di pistola. Jason non può fare altro che darsi a una fuga disperata inseguito dai fuorilegge e dai loro elicotteri. Il ragazzo cade da un ponte, ma viene salvato da Dennis Rogers, un emigrato liberiano che lo conduce presso i rakyat e iniziandolo alla tribù incidendogli un tatau. Qui Jason conosce i rudimenti della sopravvivenza dagli abitanti dell'isola che gli insegnano a combattere contro nemici umani e animali, usare veicoli, dialogare, acquistare e riparare oggetti. Dopo aver fornito supporto ad un gruppo di Rakyat alla conquista di un avamposto controllato dai pirati di Vaas, Jason viene indirizzato alla casa del Dottor. Earnhardt, dove si trova la sua amica Daisy, fuggita dai pirati e soccorsa dal Dottore.
Le conoscenze di Jason si estendono inizialmente al Dottor. Earnhardt, un portentoso chimico ritiratosi dalla vita d'un tempo, dipendente dalle stesse droghe che produce con cui combatte la profonda depressione causatagli dalla perdita prematura di sua figlia quando era in Inghilterra. L'aiuto di Jason nella ricerca dei funghi per la produzione di un antidoto per Daisy, si rivela vitale per mettere in salvo il suo primo compagno a cui confessa la tragica perdita di Grant, proprio fratello e fidanzato della stessa ragazza. Jason affida Daisy all'inizialmente scettico Earnhardt, che vedrà poi in lei l'immagine di sua figlia decidendo così di ospitare anche gli altri eventuali sopravvissuti. Il passo successivo è conoscere Citra, la seducente donna a capo dei Rakyat, che chiede a Jason un antico pugnale andato perduto da secoli. In una visione provocata dal potente allucinogeno datogli da Citra, Jason ha visioni del futuro dove vede un uomo in bianco: la chiave per aprirsi la strada verso il pugnale con cui conquisterà la piena fiducia dei Rakyat, validissimi alleati.
Quando Jason viene a sapere, grazie ad una soffiata di Dennis, che Vaas ha portato la sua fidanzata Liza in un centro di stoccaggio accorre subito facendosi strada tra i mercenari lì presenti ad aspettarlo. Viene, però, catturato e portato al cospetto proprio di Vaas. Davanti e come lui, legati ad una sedia, ci sono Liza, la fidanzata, ed Oliver, suo amico. Quest'ultimo viene portato via e, dopo aver scoperto il legame di sangue fraterno tra Citra e Vaas, Jason combatte per salvare se stesso e la fidanzata dalla casa crollante in fiamme. Nonostante avesse appiccato l'incendio, Vaas torna di nuovo alla caccia del fortunato Jason, riuscito ad evadere per la seconda volta dalle sue grinfie.
Dopo aver salvato Liza, Jason andrà in cerca dell'uomo in bianco che aveva visto nella visione al Tempio di Citra. Scoprirà essere l'agente della CIA sotto copertura Willis, incaricato di controllare gli spostamenti di Hoyt e Vaas nelle Rook Island. Grazie a lui, Jason riesce a far crollare gradualmente l'impero di Vaas: prima bruciando con un lanciafiamme i campi in cui viene coltivata la droga, poi facendo esplodere i depositi di armi. Nello stesso villaggio in cui conosce Willis, Jason viene sapere di un eccentrico australiano di nome Buck che ha comprato Keith, altro suo compagno scomparso, da Vaas. Naturalmente non riesce a convincerlo con le sole parole a rimettere in libertà il 'costoso' schiavo Keith. Buck gli propone uno scambio e gli mostra un disegno che raffigura lo stesso pugnale di cui lui stesso è già alla ricerca. Nonostante l'indecisione sul da farsi, Jason cerca e trova, dopo molte peripezie tra le antiche rovine sotterranee dell'isola, il tanto ambito pugnale. Al momento dello scambio Buck cerca di approfittare sessualmente di lui, come già aveva fatto con Keith. Lo scontro si risolve con la morte di Buck e la liberazione di Keith. Jason porta il pugnale a Citra che gli promette tutto l'aiuto possibile della sua tribù per affrontare Vaas. Dopo avergli fatto bere un altro potente allucinogeno, Citra include, con un rito ammiccante e sessuale, Jason nella tribù eleggendolo a potente ed innato guerriero e rifinendogli ancora il Tatau che ha sul braccio.
Proprio dopo il suo ingresso ufficiale nei Rakyat, Jason si fionderà con coraggio e determinazione contro Vaas per mettere fine, finalmente, al suo regime di terrore e di follia. Viene a sapere da Keith che suo fratello minore Riley è perito sotto il fuoco dei proiettili dei pirati. Spinto dall'irrefrenabile vendetta tenta di uccidere Vaas, ma fallisce e viene catturato dallo stesso. Dopo un raccapricciante monologo sulla follia, Vaas tenta di far annegare Jason legandolo ad un grosso macigno. Ancora una volta Jason scappa alla morte, fino a quando, dopo lo schianto dell'elicottero che aveva tentato di dirottare, Vaas gli sarà ancora una volta davanti. Ripetutogli lo stesso monologo di follia, incentrato sull'ossessiva ripetitività delle azioni, gli spara a sangue freddo dritto nel petto. Jason si risveglia, miracolosamente, sotto il mucchio di cadaveri aiutato da Dennis. Scopre che l'accendino donatogli dallo stesso Vaas ha fermato in tempo la pallottola altrimenti mortale. Jason approfitta della sua morte apparente per cogliere di sorpresa Vaas che uccide dopo un lungo e spettacolare combattimento con il coltello donatogli da Citra. Torna sano e salvo dai suoi amici sopravvissuti, nella caverna in cui stanno costruendo tutti insieme la nave che li porterà, finalmente, lontani da quell'inferno. Con totale rammarico della fidanzata e dei suoi amici, Jason decide di restare per chiudere una volta per tutte i conti con Hoyt, il principale signore della guerra che tira i fili nelle Rook Island. La natura selvaggia dell'isola sembra aver corrotto l'animo del giovane Jason che scopre gusto nell'uccidere e in tutto quello che gli può dare una forte scarica d'adrenalina. Ignora tutto ciò per concentrarsi sul suo obiettivo: la morte di Hoyt e la liberazione delle Rook Island.
Approfittando di un passaggio dell'uscente agente Willis, Jason si lancia dall'aereo con la tuta alare che lo porta dritto sull'isola a Sud. Qui conosce Sam Becker, un pezzo grosso dell'esercito privato di Hoyt, anche lui covante i suoi stessi scopi. Grazie ai suoi consigli, Jason riesce ad infiltrarsi di soppiatto nell'organizzazione militare e commerciale di Hoyt con un nome falso. La prima prova per ottenere la fiducia del suo 'capo' è torturare un prigioniero per fargli sputare la sua identità. Jason, suo malgrado, scoprirà essere suo fratello Riley. Per non far saltare la sua copertura e venire ucciso insieme a Sam è costretto a torturare impietosamente suo fratello minore. Questo devierà ancor di più il suo animo già corrotto dalla violenza e dal sangue che ha versato in precedenza. Appena ottenuta la fiducia di Hoyt, Jason e Sam vengono invitati ad una partita a Poker. Sfortunatamente, Hoyt uccide Sam e rivela a Jason di sapere la sua vera identità fin dall'inizio. Costretto ad un gioco perverso, Jason perde un dito staccatogli da Hoyt ad una mano sfortunata a Poker. Lo riesce, tuttavia, ad affrontare a duello col coltello ed ucciderlo. Corre, quindi, verso l'aeroporto in cui sa esserci suo fratello minore venduto ad uno schiavista in Yemen. Riesce a salvarlo e scappare con lui in elicottero (sulle note della Cavalcata delle Valchirie di Richard Wagner) dopo un violento scontro a fuoco con i mercenari di Hoyt.
Dopo una telefonata incomprensibile vocalmente, ma di terrore, di Liza, Jason scopre che la villa in collina del Dott. Earnhardt è stata rasa al suolo. Lo stesso Dottore, nei suoi ultimi respiri, rivela a Jason che i suoi compagni sono stati portati al tempio di Citra. Jason e Riley accorrono in elicottero. Qui Jason viene tramortito dai Rakyat, legato e portato all'interno del tempio. Citra confessa il suo amore per Jason, il guerriero perfetto e indistruttibile che ha curato ed addestrato. Decide quindi di rompere l'ultimo ostacolo tra lei e Jason preparando i suoi compagni, la sua fidanzata e suo fratello al sacrificio nel tempio.
Restare per sempre sull'isola o fuggire con i propri compagni? La scelta è solo del giocatore e questa determinerà uno dei due finali:
Risparmiare i compagni: Jason libera tutti i suoi compagni. Citra, presa dalla rabbia e dalla delusione, esplode in un triste pianto. Dennis, che covava lo stesso amore non corrisposto, tenta di accoltellare Jason, ma Citra si frappone fra lui e la lama mortale. La donna cade a terra e, alle parole 'Ti amo', chiude gli occhi per sempre sotto gli sguardi stupefatti dei presenti. Jason cerca, alla fine, di ricredersi e cercare nella sua persona un briciolo di umanità che, forse, non è andato perduto. Lui e i suoi compagni sono salvi, ma non la giovane Citra che l'ha aiutato...
Allearsi a Citra: con un impietoso fendente, Jason sgozza la propria fidanzata e poi intraprende sull'altare del Tempio dei Rakyat un rapporto sessuale con Citra. Proprio nel culmine di questo, Citra squarta la pancia di Jason con il suo coltello. Il suo sacrificio è completo, infatti Jason è l'ultimo ostacolo da lei ritenuto impuro (il più grande nemico di Jason è infatti se stesso) e la nascita del figlio rappresenterà la venuta del 'guerriero perfetto' nella tribù dei Rakyat, che ora dominano le Rook Island.","PlayStation 3 | PlayStation 4 | XBox 360 | XBox One | PC","Azione | Avventura | Sparatutto");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Far Cry 4","2014-11-18","Ubisoft","imgs/locandina Farcry 4.webp","","Il giocatore veste i panni di Ajay Ghale: un giovane ragazzo tornato nel Kyrat per esaudire l'ultimo desiderio della madre Ishwari, ossia spargere le sue ceneri a Lakshmana. Al suo arrivo, Ajay si trova ben presto immischiato nella sanguinosa guerra civile tra i ribelli del Sentiero d'Oro e i soldati di Pagan Min, dispotico e autoproclamato re del Kyrat.
Ajay Ghale si trova in un tram diretto a Lakshmana, nel nord del Kyrat e un uomo dall'abbigliamento inusuale: Darpan gli chiede il passaporto. Dopo un po', il tram viene fermato da alcuni soldati dell'Armata Reale, che uccidono l'autista e iniziano a sparare addosso ai passeggeri. Darpan e Ajay sono gli unici a salvarsi: ben presto fa la sua comparsa il dittatore del Kyrat, Pagan Min. Egli prende Ajay sotto la sua ala e gli spiega parte del passato del Kyrat e di sua madre. Successivamente, Darpan viene portato nel laboratori sotterranei da Paul 'De Pleur' Harmon e viene ucciso. Ajay è costretto ad affrontare una rocambolesca fuga su di un'auto, quando Sabal, uno dei leader del gruppo ribelle Sentiero d'Oro, fa irruzione nella guarnigione di Pagan.
Ajay sale su un'auto e viene scortato ad un rifugio da un soldato, che perde la vita quando l'auto si schianta contro un albero, precipitando in un burrone. Ajay si salva e prende il Kukri del suo defunto scortatore per poi ricevere l'ordine - da Sabal - di recarsi ad una torre radio. Qui, una bufera di neve mette fine ad una sparatoria e Sabal porta Ajay a Banapur, la base del Sentiero d'Oro. Qui fa la conoscenza di Badhra ed Amita, che lo considera un 'turista' e lo sottovaluta.
Per dimostrarle di essere sottostimato, Ajay distrugge una tana di lupi che attaccano il bestiame di un'anziana signora. Dopodiché disattiva una torre radio dell'Armata Reale - su richiesta di Sabal - e libera alcuni ostaggi e poi un avamposto.
Dopo aver fatto la conoscenza di Hurk Drubman Jr., Ajay compie alcune missioni per interesse dei due capi ribelli, che spesso litigano sulla strategia da adottare per rovesciare il regime di Pagan Min. Dopo essersi recato a casa Ghale (usata prima che la madre si trasferisse in America), trova Donald 'Yogi' e Reggie, che gli iniettano una sostanza allucinogena a tradimento. Al suo risveglio, Ajay si trova nudo nell'Arena di Shanat, dove viene costretto dalla Dott.ssa Noore Najar a combattere contro soldati e predatori.
La donna rivela che è stata costretta da De Pleur a divenire una luogotenente di Pagan Min perché egli aveva preso in ostaggio la sua intera famiglia. Quando Ajay si reca alla Città del Dolore per catturare Paul, questo gli dice che il marito e i figli di Noore sono stati uccisi anni fa. Paul viene interrogato dai soldati del Sentiero d'Oro e rinchiuso a Banapur.
Successivamente, Ajay fa la conoscenza di Willis Huntley, un agente sottocopertura inviato dalla CIA che recluta Ajay ed insieme mandano a segno un attentato all'aeroporto nazionale Kyrati, dove uccidono tutti i soldati a sorvegliarlo. Willis porta Ajay sull'Himalaya per uccidere un ufficiale di Yuma Lau, braccio destro di Pagan Min. A lavoro compiuto, Willis abbandona Ajay su una montagna, che viene sedato da un soldato e portato alla prigione di Durgesh. Qui, Ajay conosce Yuma e fugge dalla località, recuperando il suo equipaggiamento.
Ajay viene costretto da Amita e Sabal ad uccidere Noore. Anche Yuma subisce la stessa sorte dentro una caverna. Più tardi, Ajay partecipa all'assalto della Fortezza Reale, che riesce, per poi decidere di andare a prendere Pagan Min.
Ajay entra nella stanza da pranzo di Pagan Min, che lo invita a consumare un pasto assieme. Dopo aver parlato del passato di Ajay e averlo fatto riflettere su tutto quello che ha fatto, Pagan affida ad Ajay due scelte, che determinano il finale.
Ajay uccide Pagan Min: Ajay spara con una pistola a Pagan Min, liberando il Kyrat dalla sua tirannia, senza scoprire però l'importante significato, ignorato da Ajay, di Lakshmana.
Ajay risparmia Pagan Min: Ajay risparmia Pagan Min e quest'ultimo gli rivela che Lakshmana era la sorellastra di Ajay, figlia di Ishwari e dello stesso Pagan Min. Inoltre, Pagan rivela ad Ajay che Mohan, padre del ragazzo, uccise Lakshmana per gelosia e che fu a sua volta ucciso da Iswhari per vendetta. Dopo il colloquio, Pagan ed Ajay si dirigono in un santuario vicino, dove Ajay lascia l'urna contenente le ceneri della madre. Quando Ajay esce dal santuario, trova Pagan Min su un elicottero che lo saluta, affidandogli il Kyrat. Prima che l'elicottero si allontani definitivamente, è comunque possibile abbatterlo e quindi uccidere Pagan.
Finale alternativo: Un easter egg degli sviluppatori permette di concludere il gioco in meno di 20 minuti, tuttavia non giocando affatto. Quando Pagan Min chiede ad Ajay di attenderlo davanti alla tavola da pranzo all'inizio del gioco, il giocatore ha la possibilità di aspettarlo, invece di scappare. Dopo 15 minuti, Pagan torna da Ajay e gli racconta chi è Lakshmana. Dopo il dialogo, Pagan e Ajay si dirigono in un santuario vicino, dove Ajay lascerà l'urna contenente le ceneri della madre e, successivamente, tornerà in America. Il gioco si concluderà con i titoli di coda.","PlayStation 3 | PlayStation 4 | XBox 360 | XBox One | PC","Azione | Avventura | Sparatutto");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Far Cry 5","2018-03-27","Ubisoft","imgs/locandina Farcry 5.jpeg","","Il gioco comincia con un breve filmato introduttivo che registra il paesaggio ambientale del Montana, per poi soffermarsi su una chiesa circondata da uomini armati, in attesa che cominci qualcosa. Dopo essere entrato nella chiesa, il personaggio continua a filmare di nascosto il discorso di Joseph Seed, fino a quando non viene scoperto e ucciso da quest'ultimo. A questo punto il segnale del video si interrompe e il protagonista si rimette il telefono in tasca. Infatti il protagonista attualmente è una giovane recluta della polizia, e sta viaggiando a bordo di un elicottero verso la chiesa di Joseph Seed a Hope County, Montana. Insieme a lui ci sono lo sceriffo Whitehorse, il vice Hudson, il copilota Pratt e il marshall Burke. Una volta spiegata la situazione al protagonista, l'elicottero atterra davanti alla chiesa, circondata dai membri della setta (anche detti edeniti), che scrutano gli agenti senza però fare nulla. Prima di entrare nella chiesa, Whitehorse intima a Burke di mantenere la calma e di non prendere decisioni affrettate. Una volta entrati nella chiesa, il protagonista arresta e cerca di scortare Joseph Seed, ma la situazione degenera e l'elicottero finisce con lo schiantarsi. Tolte tutte le comunicazioni con il mondo esterno, il protagonista è costretto a scappare insieme a Burke a bordo di un pickup, che però finisce in un fiume dopo un lungo inseguimento. Burke viene rapito dagli edeniti, mentre il protagonista è soccorso da Dutch, un abitante della contea che vive in un bunker. Da qui comincia la vera e propria avventura del protagonista: egli dovrà esplorare l'intera contea, organizzando e formando una vera e propria resistenza, per cercare di fermare l'oppressione di Joseph e dei suoi fratelli John, Jacob e Faith.
Finali
Alla fine, dopo aver sconfitto John, Jacob e Faith, Dutch ci chiamerà dicendo che Joseph ci attende alla chiesa pronto alla resa dei conti. Il giocatore giunge alla chiesa e si trova faccia a faccia con il Padre, che gli mostra lo sceriffo, Hudson e Pratt in catene e minacciati dagli altri membri della resistenza sotto l'effetto del gaudio. A questo punto, Seed pone il giocatore davanti a due scelte: arrendersi e lasciare Hope Country con i suoi compagni o restare e affrontare Joseph. La trama proseguirà in base alla scelta del giocatore.

Finale 1: Se si sceglie di resistere, Joseph comincerà ad infuriarsi e, in preda all'ira, riverserà due barili di gaudio sulla zona scatenando una battaglia. Da qui il giocatore dovrà liberare i suoi colleghi, colpendoli e in seguito rianimandoli, per poi fare la stessa cosa con tutti i suoi alleati. Una volta liberati tutti, si scaglieranno contro il padre, sconfiggendolo definitivamente. Ma nel momento esatto in cui sta per essere arrestato, un'enorme esplosione nucleare si scaglia sulla contea, costringendo i protagonisti alla fuga. Durante il viaggio verso il bunker di Dutch tuttavia, l'auto su cui viaggiavano il protagonista, Whitehorse, Hudson, Pratt e Joseph si schianta, uccidendo tutti tranne il protagonista e Joseph, che afferra il personaggio e lo trascina nel bunker. Qui Joseph, dopo aver ucciso Dutch, spiega al protagonista che aveva ragione su tutto e che saranno loro due, una volta finito tutto, a ricostruire il nuovo mondo. Infine si appoggia sulla sedia guardando il protagonista dritto negli occhi.
Finale 2: Il giocatore decide di andarsene da Hope Country con i suoi compagni. Dopo un breve monologo di Joseph, in cui perdona il protagonista per aver ucciso i suoi fratelli, il vice sceriffo salirà in macchina con lo sceriffo, Hudson e Pratt. Una volta lasciata la chiesa di Joseph, lo sceriffo afferma che stanno andando dalla Guardia Nazionale per chiamare i rinforzi ed arrestare Seed, ma proprio quando lo sceriffo accende la radio parte la canzone di The Platters 'Only you' (la stessa usata da Jacob Seed come ipnosi per le sue vittime), che condiziona mentalmente il protagonista, il quale fissa lo sceriffo mentre quest'ultimo gli chiede: 'Che c'è?'. Il gioco si chiude senza specificare che cosa sia successo, lasciando tuttavia intuire che molto probabilmente il protagonista abbia ucciso i suoi compagni.

Finale segreto: Un easter egg degli sviluppatori permette di concludere il gioco in meno di 20 minuti, tuttavia non giocando affatto. Quando, all'inizio del gioco, si dovrà arrestare Joseph Seed, il giocatore ha la possibilità di non farlo. Esitando per circa 10/15 minuti, lo sceriffo intima ai suoi agenti di andare via affermando che se qualcuno si fosse azzardato ad arrestare Joseph, nessuno sarebbe rimasto vivo. Dopo che i poliziotti escono dalla chiesa, torneranno alla centrale e il gioco si concluderà con i titoli di coda.

Ambientazione
Il gioco è ambientato nella regione immaginaria di Hope County, in Montana, dove un predicatore di nome Joseph Seed è salito al potere. Seed crede di essere stato scelto per proteggere la gente di Hope County da un ''inevitabile catastrofe'' e ha fondato una setta chiamata il Progetto a Eden's Gate (ispirato al vero Heaven’s Gate). In apparenza sembrerebbe che sia stata fondata per adempiere alla sua missione di guidare il popolo alla salvezza; in realtà, Seed è un predicatore radicale e Eden's Gate è un suo culto militaristico del giorno del giudizio. Eden's Gate ha usato sia la violenza che la coercizione per convertire con forza il popolo di Hope County impedendo loro di chiedere aiuto al mondo esterno. Quando il tentativo di arrestare Seed fallisce con il rapimento di diversi poliziotti, il giocatore viene trascinato in un conflitto armato tra Eden's Gate e i cittadini in vita di Hope County, i quali stanno organizzando un movimento di resistenza.","PlayStation 4 | XBox One | PC","Azione | Avventura | Sparatutto");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Ghost Recon Wildlands","2017-03-07","Ubisoft","imgs/locandina Ghost Recon Wildlands.jpg","","Il gioco è ambientato in Bolivia nel luglio 2019. A seguito dell'aumento del potere e dell'influenza del Santa Blanca, un cartello della droga Boliviano, il paese è entrato in uno stato di grave instabilità politica al punto di diventare un narco-stato amministrato dal cartello, che lo ha trasformato nel più grande produttore di cocaina del mondo. La continua espansione del Santa Blanca anche al di fuori della Bolivia inizia a preoccupare il governo degli Stati Uniti d'America.
A seguito di un attentato dinamitardo presso l'ambasciata americana di La Paz e dell'uccisione dell'agente della DEA Ricardo 'Ricky' Sandoval, il governo statunitense dà il via all'Operazione Kingslayer, in collaborazione con CIA, DEA e JSOC. Un'unità speciale dell'esercito degli Stati Uniti chiamata 'Ghost' viene spedita in Bolivia con il compito di destabilizzare il cartello. L'unita è composta dal leader Nomad, l'artigliere Midas, l'ingegnere Holt e il cecchino Weaver. In Bolivia incontrano il loro contatto della CIA Karen Bowman, amica di Ricky Sandoval, che li introduce a Pac Katari, leader del gruppo ribelle Kataris 26, unica resistenza contro il Santa Blanca. Pac Katari chiede ai Ghost di salvare Amaru, un uomo prigioniero del cartello le cui ideologie lo hanno ispirato a fondare il Kataris 26. Dopo aver salvato Amaru, i Ghost sono liberi di eliminare il cartello in qualsiasi modo e in qualsiasi ordine.
Mentre smantellano il cartello pezzo per pezzo, ai Ghost viene data la possibilità di incontrare El Sueño, il pericoloso leader del Santa Blanca. Arrivati al punto di incontro, El Sueño contatta i Ghost attraverso un cellulare lasciato vicino a quattro auto di lusso, e gli offre la possibilità di lavorare per il Santa Blanca in cambio di grandi benefici, offerta che i Ghost rifiutano.
Dopo aver eliminato due boss delle operazioni su quattro, i Ghost vengono contattati da Pac Katari che li informa di un ribelle che possiede informazioni importanti su El Sueño. Raggiunto il punto d'incontro trovano però il cadavere di Amaru e decidono di contattare Bowman per informarla dell'accaduto, ma durante la discussione la linea cade. I Ghost tornano subito da Bowman che li avvisa che Pac Katari li ha traditi e che El Sueño si trova nel suo mausoleo, nella provincia di Pucara. Mentre i Ghost si dirigono verso il mausoleo, Pac Katari li contatta dicendogli di non voler essere una marionetta degli Stati Uniti e di voler occuparsi di El Sueño con le sue stesse mani.
Dopo essersi fatti strada attraverso l'opposizione nemica, i Ghost e Bowman trovano e circondano El Sueño che nel frattempo ha ucciso e decapitato Pac Katari. In quel momento Bowman riceve una telefonata dai suoi superiori, che la informano che El Sueño ha raggiunto un accordo con il dipartimento di giustizia per ottenere l'immunità in cambio della testa di altri cartelli rivali. In base alle azioni dei Ghost durante il gioco, ci saranno due finali diversi:
Se i Ghost non hanno ancora smantellato completamente il cartello, Bowman ucciderà a bruciapelo El Sueño, azione che le costerà la carriera e una condanna a 45 anni di reclusione. Nonostante ciò, lei non si pentirà di quello che ha fatto, avendo messo la vita di tutti i boliviani e il suo vecchio migliore amico Ricky sopra ogni cosa.
Se i Ghost hanno completamente smantellato il cartello, Bowman prenderà El Sueño sotto custodia protettiva. Durante la custodia, El Sueño fornirà informazioni importanti su altri cartelli della droga, gruppi terroristi e spacciatori d'armi. Bowman prevede che dopo che le informazioni finiranno, El Sueño verrà estradato in Messico e avvierà un nuovo cartello della droga, evenienza per cui i Ghost si dovranno nuovamente preparare.","PlayStation 4 | XBox One | PC","Azione | Sparatutto");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("God Of War Ragnarok","2022-11-09","Santa Monica Studio","imgs/locandina God Of War Ragnarok.jpeg","","Ambientato tre anni dopo gli eventi del precedente capitolo, Kratos e Atreus sopravvivono al Fimbuwinter insieme a Mimir. Atreus, dopo aver scoperto di essere un gigante, desidera esplorare il mondo alla ricerca di risposte circa la sua identità Jotunn di Loki, tuttavia Kratos glielo impedisce per timore di una guerra contro Asgard. Di ritorno da una battuta di caccia, i tre riescono a fuggire da un attacco di Freya, (la quale è in cerca di vendetta per la morte del figlio Baldur), grazie a un incantesimo di protezione lanciato da Atreus. Ritornati a casa, Atreus assiste alla morte di un piccolo Fenrir, involontariamente lancia una magia, lo seppellisce e scompare nel nulla. Kratos e Mimir, preoccupati per il mancato ritorno del ragazzo, si recano a cercarlo. Durante la ricerca si imbattono nell'orso Bjorn, il quale si rivela essere Atreus stesso, trasformato. Padre e figlio, tornati a casa, ricevono la visita di Thor e Odino, i quali chiedono al ragazzo di interrompere la sua ricerca di Tyr e offrono a Kratos pace e la cessazione degli attacchi di Freya. Kratos rifiuta, così il dio della guerra è costretto ad affrontare il dio del tuono, mentre Odino discute privatamente con Atreus, invitandolo ad Asgard. Il figlio di Odino interrompe prematuramente il suo scontro con lo spartano,e Kratos si precipita a casa grazie all'aiuto dei nani Brok e Sindri, tuttavia è indispettito per le azioni di Atreus. Lo Jotunn chiede al padre e a Mimir di seguirlo e li conduce presso un altare Jotunn vicino la loro casa. Lì, apre un portale accessibile solo ai giganti e mostra che i lupi Skoll e Hati (incatenati da Odino per avere più controllo sul Ragnarok) sono stati liberati dai giganti a Vanaheim e che Tyr guiderà l'esercito che affronterà gli Aesir durante il crepuscolo degli dei. Così i tre si recano nelle miniere di Svartalfheim e liberano Tyr, tuttavia quest'ultimo si rifiuta di entrare in guerra contro Asgard e i quattro tornano a casa di Sindri, costruita nel regno fra i regni. Nel mentre Kratos si riposa, Atreus, accompagnato da Sindri si reca a Midgard, da Jormungandr e da Freya, nel tentativo di trovare delle risposte, ma purtroppo torna dal nano a mani vuote. Tornati a casa di Sindri, il ragazzo si riunisce al padre e a Tyr, il quale suggerisce di cercare chiarimenti in un altare Jotunn, ad Alfheim. Giunti nel regno degli elfi chiari, raggiungono l'altare ed entrano nel portale aperto da Atreus e scoprono che il Ragnarok porterà alla morte di Odino e alla distruzione della sola Asgard, permettendo ai regni rimanenti di prosperare; tuttavia Atreus trova nuovamente il rifiuto di Tyr di condurre l'esercito che marcerà contro Asgard.
Tornati a casa di Sindri, Atreus riesce a viaggiare fino al regno di Jotunheim. Qui, incontra la gigantessa Angrboda, la quale gli racconta la sua storia, la sua missione e la profezia che vede Kratos perire tra le braccia del figlio a causa di Thor e Atreus allearsi con Odino ad Asgard. Prima di tornare a casa, la gigantessa strappa al ragazzo di promessa di mantenere l'incontro segreto. Atreus torna a casa a Midgard e mentre sta per tornare a casa di Sindri, viene raggiunto da un arrabbiato Kratos, il quale lo accusa di essersi recato ad Asgard. In quel momento, i due vengono aggrediti da una valchiria che si rivela essere Freya. Nel tentativo di impedire alla dea di aggredire il padre, Atreus si trasforma in un orso e cerca di uccidere la donna, ma viene fermato da Kratos. Mentre Atreus, Brok e Sindri tornano a casa di quest'ultimo, Freya chiede a Kratos e Mimir di aiutarla a raggiungere Vanaheim e di distruggere l'incantesimo lanciato da Odino per tenerla imprigionata a Midgard. Raggiunto il regno dei Vanir e distrutto l'incantesimo, Freya comprende che Odino è il vero nemico, così stringe una nuova alleanza col Fantasma di Sparta, tornano a casa del nano. Kratos e Atreus hanno una discussione molto accesa, che vede il primo accusare il figlio di mentirgli e di essersi recato ad Asgard, e il secondo accusare il padre di non fidarsi più di lui. In preda alla rabbia, il ragazzo si trasforma in orso, ferisce Sindri e raggiunge Asgard. Qui, Atreus scala le mura della città e conosce Heimdall, il quale vuole uccidere il ragazzo, in quanto comprende il desiderio di questo di tradire Odino, tuttavia viene fermato da Thor. Il Padre di Tutti raggiunge i tre, felice della scelta del gigante di recarsi nel regno degli Aesir e accompagna Atreus nella stanza di Modi, dove conosce Thrud e Sif (rispettivamente figlia e moglie di Thor). Odino chiama il ragazzo nel suo studio e lo accompagna nella sua biblioteca privata, dove racconta di una crepa creatasi dall'uccisione di Ymir che custodice tutte le risposte e tutte le verità, e gli mostra una maschera che permette di guardarvi attraverso. Atreus riesce a tradurre la lingua scritta sul frammento di maschera, e si reca accompagnato da Thor nel regno di Muspelheim. Qui, riesce a trovare un altro frammento della maschera e i due tornano ad Asgard.
Nel frattempo, Kratos, accompagnato da Mimir e Freya, giunge a Midgard nel tentativo di trovare le Norne e farsi rivelare il destino che attende al figlio. Queste gli confermano la veridicità della profezia che Kratos trovò a Jotunheim quando giunse con Atreus per spargere le ceneri della moglie, e gli rivelano che il figlio verrà ucciso da Heimdall. Così Kratos decide di uccidere Heimdall prima che questo possa stroncare la vita di Atreus e, insieme a Brok si reca nel regno dei nani per forgiare la lancia Draupnir. Sulla strada del ritorno, Kratos viene intercettato da Odino e cominciano a parlare della possibilità di evitare Ragnarok. Lo spartano chiede a Odino di restituirgli suo figlio, tuttavia il Padre di tutti si rifiuta e fa ritorno ad Asgard, mentre Kratos riprende il cammino verso casa di Sindri.
Contemporaneamente, Atreus decifra la scritta sul secondo frammento recuperato della maschera e suppone che il frammento mancante si trova a Helheim. Odino invia Atreus nel regno dei morti e lo fa accompagnare da Thrud e Heimdall. Qui, seguendo l'energia mistica della maschera, raggiunge il luogo in cui è incatenato il lupo Garm e lo libera. Purtroppo la liberazione di Garm comporta gravissime conseguenze nei nove regni, in quanto il lupo ha il potere di distruggere il tessuto dei regni. Ritornati ad Asgard, Atreus chiede al Padre di Tutti di tornare da suo padre. Riconciliatisi, padre e figlio tornano a Helheim e affrontano Garm (il quale ha già aperto delle fratture nei nove regni), uccidendolo, ma purtroppo, sulla strada del ritorno, i due vengono attaccati nuovamente dal lupo, il quale è tornato in vita. Così Atreus tenta una magia già usata durante il suo soggiorno a Jotunheim su un serpente, sul lupo e riesce a domarlo. Infatti nel lupo è stata inserita l'anima di Fenrir, il quale grazie ai poteri di Garm, apre un portale fra i regni e torna a casa a Midgard. I due, tornati a casa dei nani, decidono di recarsi a Vanaheim in soccorso di Freya e Freyr, catturato dalle forze degli Aesir. Qui, Kratos e la donna vengono rallentati da un sopraggiunto Heimdall, così lo scontro tra il guardiano degli Aesir e il Fantasma di Sparta comincia. Kratos riesce a sconfiggere Heimdall grazie alla lancia Draupnir, ma purtroppo è costretto a ucciderlo. Terminato lo scontro, lo spartano raccoglie il corno Gjallarhorn e si riunisce al gruppo del figlio, dove riesce a fare breccia tra le forze di Asgard e trarre in salvo Freyr. Il gruppo, ritornato ancora a casa di Sindri decide di mandare Atreus di nuovo ad Asgard come spia, per scoprire quante più informazioni possibili sulla maschera e sottrarla ad Odino.
Tornato ad Asgard, il ragazzo comprende l'indizio sulla maschera e viene incaricato da Odino di recarsi a Niflheim e di trovare Thor. Atreus riesce a trovare Thor con l'aiuto di Thrud in una taverna in evidente stato di ebbrezza. Dopo aver distrutto la taverna durante una rissa, Atreus e Thrud riescono a portare Thor fuori dal locale e Atreus cerca di aiutare un dio del tuono emotivamente provato dalle delusioni di moglie e figlia nei suoi confronti e dalle continue mancanze di rispetto dal parte del Padre di Tutti. Giunti nel regno di ghiaccio, i due riescono a trovare il frammento mancante e Thor salva Atreus da una caduta fatale. In quel momento arrivano Odino accompagnato da due Valchirie e Sif, la quale informa il Padre di Tutti che il responsabile della morte di Heimdall è Kratos, e per timore che Thrud possa seguire la stessa sorte toccata a Magni e Modi, Sif chiede a Thor di uccidere Kratos e Atreus. Quest'ultimo riesce a tornare sano e salvo con la maschera dal padre grazie a un incantesimo di Sindri. Così i due, insieme a Mimir, Brok, Sindri, Freya, Freyr e Tyr decidono di usare la maschera per fare in modo che Odino non ottenga le risposte che cerca da tutta la vita. Tyr si offre di guidarli in quella missione attraverso un passaggio segreto verso Asgard e Brok nota un cambiamento nel dio della guerra che non lo convince. Purtroppo quando il nano incalza il dio per ottenere più informazioni, la verità viene a galla: dietro a Tyr c'è sempre stato Odino, il quale ha assunto le sembianze di suo figlio per spiare il gruppo dello spartano, pugnala a morte Brok e prende in ostaggio Atreus. Freya e Sindri si occupano di prestare le cure a Brok, mentre Freyr e Kratos affrontano Odino, il quale chiede la restituzione della maschera. Freyr decide di consegnarli la maschera, ma quando Odino sta per fuggire, Kratos lo attacca con la lancia, riuscendo a strappargli dalle mani la machera. Impotente, il gruppo assiste alla morte di Brok, il quale spira fra le braccia del fratello e Sindri scompare col corpo del nano blu. Kratos e Atreus raggiungono Sindri nel tempio di Tyr, il quale decide di abbandonare i due, accusandoli della morte del fratello. Padre e figlio decidono che è arrivato il momento della guerra contro Odino e si riuniscono al gruppo a casa di Sindri. Qui, Freyr e Freya, accompagnati da Mimir, si dividono per formare un esercito con le forze di Vanaheim, Alfaheim, Helheim e le valchirie ribellatesi a Odino, un ritrovato Sindri si offre di formare un esercito di nani, mentre Kratos e Atreus si recano a Muspelheim da Surtr per convincerlo ad unirsi al loro esercito. Dopo un iniziale rifiuto, questo decide di aiutare i due, trasferendosi nelle armi di Kratos, così quando il corno verrà suonato, Ragnarok aiuterà il gruppo nella distruzione di Asgard.
Completata la formazione dell'esercito, il gruppo senza Sindri, si riunisce nel tempio di Tyr e Kratos suona il corno di Heimdall, dando inizio alla guerra contro Asgard. Giunti nel regno di Odino, alla battaglia si uniscono anche Ragnarok, Angrboda con Fenrir e Jormungandr, il quale tiene impegnato Thor, ma di Sindri e dei nani nessuna traccia. Kratos e Freya giunti nei pressi della torre di Svartalfheim vengono raggiunti da Sindri, il quale con un'arma di sua invezione, riesce a penetrare le mura che circondano la città delgi Aesir. Al gruppo si aggiungono anche Thrud, la quale aiuta Atreus nel salvare quanti più innocenti possibile e Sif. Nel frattempo lo scontro tra il serpente del mondo e il dio del tuono è talmente violento che scuote le fondamenta di Yggdrasill spedendo il serpente indietro nel tempo. Senza il pericolo del serpente, Thor si lancia contro Kratos in un maestoso scontro finale difronte alla casa di Odino. Kratos riesce a sconfiggere Thor, ma decide di non ucciderlo, invitandolo a cambiare e unirsi a loro. Putroppo in quel momento giunge Odino il quale ordina al figlio di uccidere lo spartano. Dinanzi all'ultima mancanza di rispetto, Thor decide di ribellarsi, trovando la morte per mano del suo stesso padre. Alla morte del dio del tuono, assiste Thrud la quale nel tentativo di afferra Mjolnir, viene colpita dal Padre di Tutti. Così inizia l'epica battaglia finale tra il Fantasma di Sparta con suo figlio contro Odino. Lo scontro è molto acceso e i tre precipitano nella biblioteca privata del Padre degli Dei, dove si aggiunge anche Freya, la quale in un ultimo attacco, sfrutta la corda (recuperata durante il viaggio verso le Norne) con cui Odino si è impiccato per conoscere i segreti della morte, ancora ricolma di una magia deleterea per il dio. Atreus decide di distruggere la maschera che avrebbe permesso di ottenere una conoscenza virtualmente illimitata e la verità sulla creazione stessa. Odino si lancia in un ultimo disperato attacco, tuttavia dalla battaglia ne esce sconfitto. Atreus, grazie alla magia Jotunn, estrae l'anima di Odino, la imprigiona e la consegna a Freya, ma lei decide di restituirla al ragazzo, ma in quel momento giunge Sindri che distrugge l'anima di Odino, uccidendolo. Risolta la battaglia si presenta il problema della furia incontrollata di Ragnarok, il quale cerca di distruggere Asgard. Per concedere ai suoi alleati il tempo necessario per fuggire, Freyr decide di sacrificarsi, rimanendo ad Asgard, mentre il mostro affonda la sua spada nel cuore del regno, distruggendolo. Atreus si risveglia a Midgard e parte alla ricerca del padre. Lungo la strada, si incrocia con le Valchirie, Thrud e Sif, gli alleati di Vanaheim, di Alfheim, Freya, Angrboda e Mimir. Dopo poco riesce a trovare il padre al quale gli mostra un altare nascosto dove viene mostrata la storia di Kratos e Atreus. Giunti alla fine del viaggio, il ragazzo chiede al padre di poter partire per un viaggio da solo alla ricerca dei giganti sopravvissuti nei regni sopravvissuti al Ragnarok, per poter riportare in vita la sua gente. Kratos acconsente e assiste alla partenza del figlio, ritenendolo oramai pronto ad affrontare i pericoli del mondo da solo.
Nell'epilogo, Kratos trova un altro altare, alle spalle di quello che ha visto insieme al figlio, dove osserva lui prestare soccorso nei nove regni in nome della pace, e decide di partire anche lui, accompagnato dal fidato amico Mimir e da Freya.","PlayStation 4 | PlayStation 5","Avventura");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("God of War","2018-04-21","Santa Monica Studio","imgs/locandina God Of War.jpeg","","La serie di God of War è ambientata sulla Terra nei tempi antichi ed è abitata da divinità e creature mitologiche. Ogni regione del mondo è governata dagli Dei locali come sovrani e in tale zona vivono personaggi e creature della suddetta mitologia.
Fino a God of War: Ascension la serie è ambientata nell'Antica Grecia dove a governarla sono gli Dei Olimpici guidati da Zeus. La Grecia è composta da tre piani astrali: l'Ade, dove si recano le anime dei morti; la terra, dove vivono i viventi; il Monte Olimpo dove vivono le divinità. Alla fine di God of War III la Grecia è completamente devastata dalle azioni di Kratos che ha sterminato tutti gli Dei Olimpici facendo precipitare nel caos la sua terra.
Da God of War (2018) la serie è ambientata in Scandinavia dove a governarla sono gli Dei Norreni chiamati Aesir e sono guidati da Odino. Tale luogo viene chiamato anche Midgard. Questi è diviso in nove regni che si possono raggiungere tramite la camera Tyr utilizzando il Bifrost e sono: Asgard, Alfheim, Jotunheim, Helheim, Muspelheim, Niflheim, Vanaheim e Svartalfheim. Il nono regno è Midgard.","PlayStation 4 | PlayStation 5 | PC","Avventura");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Grand Theft Auto V","2014-11-18","Rockstar Games","imgs/locandina Grand Theft Auto 5.webp","Banner/banner_gtav.jpeg","North Yankton, 2004. Nella cittadina di Ludendorff, Trevor Philips, Michael Townley e Bradley Snider effettuano una rapina presso un deposito locale ma le cose non vanno come previsto. Durante la fuga, Michael e Brad vengono colpiti da un cecchino, mentre Trevor scompare nella nebbia inseguito dalla polizia.
Nel 2013, si scopre che Michael è ancora vivo e si è fatto una nuova vita a Los Santos (controparte di Los Angeles) e che frequenta regolarmente uno psicologo, con il quale si sfoga della sua difficile situazione familiare.
Franklin Clinton, giovane criminale di strada legato a un gruppo di gang locali conosciuto come 'Le Famiglie' (The Families), riceve l'ordine da Simeon Yetarian, truffatore e titolare armeno del concessionario in cui lavora, di recuperare un'auto da James 'Jimmy' De Santa, figlio di Michael, poiché quest'ultimo è in ritardo con il pagamento delle rate della macchina. Avendo capito che il figlio sta per essere derubato, Michael si nasconde nell'auto che Franklin deve recuperare; il giovane viene quindi sorpreso e costretto sotto minaccia a irrompere nel concessionario, cosicché Michael possa regolare i conti con Simeon. Dopo questo evento Franklin viene licenziato.
Poco tempo dopo, ormai disoccupato, Franklin, che Michael aveva formalmente invitato, si reca di nuovo a casa di quest'ultimo e, con la scusa di una bevuta, gli chiede se può aiutarlo a trovare un lavoro. Dapprima restio, Michael si convince comunque a fare due chiacchiere con lui e accetta. Proprio mentre si recano al bar nei pressi, arriva però la chiamata del figlio Jimmy che, all'insaputa del padre, ha tentato di vendere il suo yacht e ora si trova immischiato in un tentativo di furto. Dopo un rocambolesco inseguimento in auto lungo l'autostrada, Michael riesce a salvare Jimmy grazie all'aiuto acrobatico di Franklin, ma non a recuperare lo yacht a causa di un'avaria al motore dell'auto di Amanda, la moglie di Micheal.
Tempo dopo, Michael scopre che sua moglie lo sta tradendo con il suo istruttore di tennis. Insieme a Franklin, Michael insegue l'amante fino a una villa, demolendola in preda alla rabbia. La villa apparteneva però a un trafficante di droga messicano, Martin Madrazo, che esige un risarcimento di due milioni e mezzo di dollari per i danni subiti. Franklin da subito si rende disponibile a dargli una mano e questo fa sì che il giovane entri definitivamente nelle grazie dell'ex rapinatore, cementando un reale rapporto di amicizia. Ritrovatosi immerso nei debiti e avendo esperienza di 'un solo modo per guadagnare soldi', Michael decide di tornare in attività. I due saldano il debito con Martin grazie al denaro ottenuto da una rapina alla gioielleria Vangelico, organizzata con l'aiuto di un vecchio amico di Michael, Lester Crest.
Trevor Philips, l'altro sopravvissuto della rapina di Ludendorff, vive ora a Sandy Shores, nella Blaine County, dove in combutta con gli amici Ron Jakowski, Wade Hebert e Chef gestisce la TPI (Trevor Philips Industries), una società dedita alla produzione e allo spaccio di metanfetamine. Guardando un servizio giornalistico alla TV, Trevor viene a sapere del colpo, e capisce che Michael, del quale aveva presenziato al funerale, è in qualche modo coinvolto, per cui riesce a rintracciarlo scoprendo che, nel frattempo, aveva cambiato cognome da Townley a De Santa, e si riunisce al suo, pur restio, vecchio amico dopo essersi temporaneamente trasferito a Los Santos insieme a Wade.
A questo punto, i protagonisti iniziano a perdere il controllo delle loro vite. L'inaspettato ritorno di Trevor rende Michael sempre più intrattabile e nervoso, il che spinge la sua famiglia a lasciarlo. Franklin inizia a contemplare l'idea di abbandonare la vita da gangster di strada e di separarsi dall'amico d'infanzia Lamar Davis, che è costantemente invischiato in lotte con i Ballas, una gang rivale, ed è sotto l'influenza di Harold 'Stretch' Joseph, un tempo rispettato veterano delle Famiglie. Trevor tenta di consolidare il suo contrabbando di metanfetamine a Blaine County, ma si scontra con diverse organizzazioni criminali e non: i Lost MC (che si inimica uccidendo Johnny Klebitz), i fratelli O'Neil (ai quali fa esplodere il laboratorio), la Triade cinese guidata da Wei Cheng e l'agenzia di sicurezza privata Merryweather Security Consulting.
L'FIB scopre che Michael è coinvolto nella rapina alla gioielleria e, per evitare l'arresto, l'uomo viene costretto dagli agenti corrotti Dave Norton, Steve Haines e Andreas Sanchez a portare a termine una serie di operazioni insieme a Franklin e Trevor per ostacolare un'agenzia rivale, l'IAA (International Affairs Agency, controparte di un mix fra l'NSA e la CIA). Le due agenzie sono infatti coinvolte in una guerra burocratica per determinare chi dovrà ricevere un aumento dei fondi da parte del governo statunitense.
Dopo aver recuperato da un camion blindato dei soldi diretti alla IAA, Michael li porterà a un contatto di Steve, il miliardario Devin Weston, il quale, in cambio dei suoi servigi, gli fa conoscere Solomon Richard, direttore dello studio cinematografico Richards Majestic. Michael è infatti fortemente motivato da un'intensa passione per il cinema e, dopo aver svolto alcuni lavori per conto dello studio, si guadagna la fiducia di Solomon e diventa produttore associato del film Meltdown, in prossima uscita.
Intenzionato a conoscere meglio Franklin, Trevor si unisce a lui e Lamar in uno scambio di droga con i Ballas, organizzato da Stretch a Grove Street. Proprio grazie alla maggiore esperienza e all'aiuto di Trevor si viene a scoprire che i Ballas volevano truffare i due giovani vendendo loro un blocco di gesso spacciato per cocaina. Smascherata la truffa, il trio cade vittima di un agguato dei Ballas che degenera in una grossa sparatoria per le strade del quartiere in cui viene coinvolta anche la polizia e i tre approfittano della confusione per scappare. Franklin cerca di convincere Lamar che Stretch li ha traditi e ha cercato di ucciderli ma l'amico non ne è ancora pienamente convinto.
A seguito di diverse operazioni più o meno riuscite, Michael e Trevor sono costretti a nascondersi temporaneamente a Sandy Shores quando, dopo aver eseguito un compito per conto di Martin Madrazo ed essersi visti rifiutare il dovuto pagamento, Trevor ha la sorprendente iniziativa di rapire Patricia, la moglie del boss. Viene inoltre messa in atto una nuova rapina, stavolta presso la banca locale nella cittadina di Paleto Bay, per finanziare un'ulteriore operazione di Steve Haines, che vuole infiltrarsi nei laboratori di ricerca Humane e rubare del gas nervino che servirebbe all'IAA per simulare un attacco terroristico e ricevere ulteriori fondi.
Nel frattempo, Trevor organizza un colpo su un treno della Merryweather che sta trasportando un oggetto di valore non meglio identificato. Il colpo si rivela un successo, ma Michael cerca di convincere Trevor a consegnare la refurtiva a Martin e liberare Patricia, così da poter far pace con il boss messicano; quando questi rifiuta perché innamoratosi di lei, Michael gli promette che, se la consegnerà, si organizzerà con Franklin e Lester per realizzare il loro più grande sogno: rapinare la Union Depository, la principale banca di Los Santos, nonché la più grande della West Coast. Trevor accetta a malincuore e i due tornano a Los Santos dopo essersi riappacificati con Martin Madrazo.
Dopo qualche tempo e alcune discussioni con Michael, Trevor inizia a sospettare che Brad, l'altro complice della rapina di Ludendorff, non sia in prigione come ha sempre creduto, ma morto e seppellito nella bara inumata di Michael. Dopo l'ennesimo litigio con quest'ultimo, Trevor si reca al cimitero di Ludendorff, scoprendo l'amara verità mentre viene raggiunto da Michael: il cecchino che ha ucciso Brad altri non è che l'agente Dave Norton, che al tempo si era accordato con Michael per far fallire il colpo e simulare la sua morte, così che potesse iniziare una nuova vita con la sua famiglia, mentre Dave si sarebbe preso tutti i meriti per l'uccisione di Michael Townley. Sentendosi tradito dall'amico, Trevor lo minaccia, ma a un tratto alcuni uomini capitanati da Wei Cheng, leader della Triade di Los Santos in lotta da tempo con Trevor, tendono loro un agguato da cui quest'ultimo riesce a fuggire mentre Michael viene rapito. Di ritorno a Los Santos, Trevor viene contattato da Wei che lo minaccia di uccidere Michael se egli non consentirà alla Triade di espandersi a Blaine County, ma Trevor mostra completa indifferenza e respinge le sue richieste, troncando il rapporto, e mettendo in forse i preparativi per il colpo alla Union Depository. Franklin, dopo aver scoperto da Lester che Michael è scomparso, riesce a rintracciarlo e salvarlo burrascosamente prima che Wei possa ucciderlo.
Gli agenti Steve e Dave si ripresentano e affidano a Michael e Franklin un ultimo lavoro: infiltrarsi dentro la sede della West Coast dell'FIB e distruggere delle prove che potrebbero compromettere Steve, tenuto sotto stretta sorveglianza dai suoi superiori a causa dei suoi metodi “poco ortodossi”. Durante l'assalto alla sede dell'FIB, Michael ne approfitta per cancellare anche i dati a suo sfavore, liberandosi dal giogo di Steve.
Dopo il lavoro, Dave si incontra con Michael al Kortz Center, con l'intenzione di aiutarlo a liberarsi di Steve, ma quest'ultimo raggiunge i due insieme all'agente Sanchez e una divisione corrotta dell'FIB, per arrestare Michael, ma a loro volta vengono interrotti dall'IAA che, insieme al doppiogioco di Sanchez, tenta di arrestare Steve per 'attività anti-americane' e poco dopo dalla Merryweather, che nel frattempo sta dando la caccia ai responsabili del colpo al loro treno. Inizia un conflitto a fuoco fra le tre agenzie e Dave e Michael ne approfittano per scappare. Trevor li raggiunge e li aiuta a mettersi in salvo, affermando che solo lui ha il diritto di uccidere Michael, facendo intendere tuttavia, senza ammetterlo esplicitamente, che in fondo ha perdonato l'amico. Nonostante ciò, nel prosieguo del gioco manterrà comunque un atteggiamento di acida freddezza nei confronti di Michael.
La famiglia di Michael, dopo il periodo di allontanamento, decide di tornare a casa e di perdonarlo, dandogli una seconda possibilità. La Richards Majestic ha nel frattempo terminato le riprese di Meltdown e il film è pronto per una prima all'Oriental Theater di Vinewood. Devin Weston, uno dei principali azionisti, ha però intenzione di boicottare il film per far fuori Solomon, comprare lo studio cinematografico e demolirlo per costruirci un complesso residenziale. Devin affida alla sua avvocatessa Molly Schultz il compito di sbarazzarsi della bobina del film, in attesa di ordini su come distruggerla. Michael si ribella a Devin e insegue la donna fino al Los Santos International Airport. Raggiunto l'hangar di Devin dopo un lungo inseguimento, Molly viene accidentalmente risucchiata dentro la turbina di un aereo, finendo triturata. Telefonando a Solomon, Michael scopre che la morte di Molly è stata inutile, dal momento che lo staff del regista dispone di svariate copie digitali del film.
Lamar, nel frattempo, cade vittima ancora una volta di una trappola di Stretch e viene rapito dai Ballas. Franklin rintraccia il possibile luogo in cui Lamar è detenuto, una segheria abbandonata di Paleto Bay usata dai Ballas come copertura per la produzione di droga. Con l'aiuto di Michael e Trevor, Franklin attacca la segheria e riporta Lamar a Los Santos.
Congedatosi dall'amico dopo un'accesa discussione, lo raggiungono gli agenti Steve e Dave che gli ordinano di uccidere Trevor, in quanto considerato inaffidabile e pericoloso per loro. La sera stessa, Michael si reca insieme a Jimmy alla prima di Meltdown; mentre attendono l'arrivo di Amanda e Tracey, giunge anche Devin che, arrabbiato con Michael per la morte del suo avvocato, annuncia ai due che ha mandato a casa loro una squadra della Merryweather per uccidere le due. Michael e Jimmy abbandonano il cinema e si recano dalle due donne, riuscendo a salvarle.

Messa al sicuro la famiglia fuori città, Michael annuncia a Lester che i preparativi per il colpo alla Union Depository possono ripartire. I tre riescono quindi a mettere a segno il colpo del secolo. Dopo la rapina, Devin Weston si presenta a casa di Franklin e gli ordina di uccidere Michael, ma il ragazzo gli rivela che Steve Haines gli ha già chiesto di uccidere Trevor. Devin quindi costringe Franklin a scegliere chi uccidere dei due. A questo punto, in base alla scelta del giocatore, la trama proseguirà su uno dei tre filoni narrativi:
A: Uccidere Trevor (Il terzo incomodo);
B: Uccidere Michael (L'allievo e il maestro);
C: Fare una follia (Conti in sospeso).

Finale A
Franklin decide di obbedire a Steve e Dave e chiama Trevor, chiedendogli di incontrarlo al giacimento di petrolio nel quartiere di El Burro Heights. Subito dopo, il giovane chiama Michael in soccorso, e lui, appresa la situazione, accetta di aiutarlo.
Franklin si incontra con Trevor, che però capisce le intenzioni del giovane e fugge in auto. Dopo un breve inseguimento, giungono in un deposito di carburante fuori città, dove arriva Michael che con la sua auto manda fuori strada il mezzo di Trevor, il quale si schianta contro una cisterna di benzina, facendone fuoriuscire il contenuto. Franklin spara con una pistola su Trevor che, essendo ricoperto di benzina, muore avvolto dalle fiamme con la cisterna stessa che esplode (vi è anche la possibilità di non sparare, ma in quel caso lo farà Michael). Michael e Franklin si convinceranno di aver fatto una buona cosa uccidendo Trevor, poiché prima o poi li avrebbe messi nei guai a causa del suo stile di vita incontrollabile. Franklin giudicherà l'episodio istruttivo, anche se i rapporti con Michael si troncheranno per volontà di quest'ultimo che, volendo tagliare col passato, non vorrà mai più avere niente a che fare con Franklin.
Lester divide la parte del bottino della Union Depository di Trevor tra Franklin e Michael, con la famiglia di quest'ultimo che torna definitivamente a casa. Franklin chiama Steve per comunicargli la notizia e gli chiede di convincere Devin a lasciar perdere Michael. Steve accetta di occuparsi di Devin e in cambio chiede a Franklin di non contattarlo mai più.

Finale B
Franklin decide di obbedire a Devin Weston e chiama Michael, chiedendogli di incontrarlo in un campo di satelliti sulla Route 68. Subito dopo il giovane chiama Trevor per chiedere aiuto ma egli, appresa la situazione, si rifiuta di aiutarlo in quanto, essendo stato tradito più volte, non vuole diventare anche lui un traditore.
Una volta faccia a faccia con Michael, questi riceve una chiamata da Amanda che gli rivela che Tracey si è finalmente iscritta al college. Michael è contento ma subito si dispera quando Franklin gli rivela di dipendere da Devin, e fugge fino alla centrale elettrica Palmer-Taylor, dove inizia un burrascoso inseguimento a piedi tra i due protagonisti che termina in cima alla torre più alta della centrale. Qui inizia un combattimento fra Michael e Franklin, alla fine del quale il secondo spinge il primo giù dalla torre, uccidendolo a causa dell'elevata altezza (vi è anche l'opzione di salvare Michael, ma quest'ultimo dà una testata a Franklin che gli fa mollare la presa).
Franklin, provando rimorso per quello che ha fatto, chiama Lamar e gli annuncia di essere tornato alla sua vecchia vita, ma viene a sapere che l'amico è costretto a nascondersi per sfuggire ai Ballas e a Stretch. Lester manda la parte del bottino della Union Depository di Michael alla famiglia di quest'ultimo, che vende la casa e si trasferisce altrove. Trevor, infuriato per il tradimento di Franklin, decide di troncare definitivamente i rapporti con il giovane.

Finale C
Franklin decide di rifiutare l'ordine sia di Devin che di Steve e cerca invece di salvare i due compagni. Per ottenere consiglio va a trovare Lester, che spiega a Franklin che l'unico modo per salvare i due è fare fuori coloro che hanno ordinato la loro uccisione, ma non prima di aver tolto di mezzo tutti i loro uomini. Il piano consiste nel far credere alla divisione corrotta dell'FIB, capitanata da Steve Haines, e alla sezione della Merryweather controllata da Devin che il bottino della Union Depository si trova in una fonderia nel quartiere di Cypress Flats, così da far venire in forze entrambe le agenzie e tender loro sul posto una trappola.
Mentre Lester avverte Michael e Trevor e organizza tutto, Franklin chiama Lamar in soccorso e questi, appresa la situazione, accetta di aiutarlo. Raggiunta la fonderia, dove sono presenti anche Michael e Trevor, i quali hanno una colluttazione sedata però da Franklin, i quattro attendono l'arrivo dell'FIB e della Merryweather. L'imboscata ha successo e il gruppo riesce a uccidere tutti gli uomini al servizio di Steve e Devin.
Lamar si congeda dal trio e i tre iniziano a dare la caccia ai nemici rimasti, così da chiudere definitivamente tutte le questioni aperte. Michael uccide Stretch, che continua a dare la caccia a Lamar assieme a un gruppo di Ballas, Trevor uccide Steve Haines, che sta girando una nuova puntata di 'The Underbelly of Paradise' per la CNT sulla ruota panoramica del molo di Del Perro, e Franklin uccide Wei Cheng, l'ultimo rivale di Trevor rimasto, e la sua scorta. La richiesta da parte di Trevor di uccidere anche Dave Norton viene respinta da Michael, in quanto Dave è l'unico in grado di evitare che il trio abbia ulteriori problemi con l'FIB.
Rimane solo Devin Weston, che viene rapito da Trevor, rinchiuso nel bagagliaio di un'auto e portato su una scogliera poco fuori Paleto Bay. Michael e Franklin raggiungono Trevor e, dopo aver deriso Devin, spingono l'auto giù dalla scogliera uccidendolo. Dopo quest'ultimo evento i tre decidono di non lavorare più insieme, rimanendo però amici.
Lester manda al trio la loro parte del bottino della Union Depository e la famiglia di Michael torna definitivamente a casa. Donald 'Don' Percival, il presidente della Merryweather, manda un'email a Michael e Trevor, ringraziandoli per aver ucciso Devin, in quanto ha potuto comprare le sue azioni a basso costo e guadagnare un sacco di soldi; promette inoltre ai due che la Merryweather non darà più loro la caccia, a patto che non interferiscano più con le loro operazioni. Anche Dave Norton contatta i due e promette loro che farà in modo che l'FIB non dia loro più fastidio, a patto che non causino ulteriori problemi (è stato confermato, dopo un update alla modalità online del titolo, che è questo il finale canonico. Lo si può intuire da una frase pronunciata da Franklin nel DLC in questione, che conferma che Michael è vivo. Anche nell'update Smuggler's Run del 2018 abbiamo una frase che testimonia la canonicità del finale, detta da Ron Jakowski per confermare che Trevor era ancora in vita).","PlayStation 3 | PlayStation 4 | XBox 360 | XBox One | PC","Azione");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Marvel's Spider-Man Miles Morales","2020-11-12","Insomniac Games","imgs/locandina Marvel's SpiderMan Miles Morales.webp","","Un anno dopo gli eventi di Spider-Man, e dopo essere stato addestrato da Peter Parker (come visto nella scena finale del DLC Silver Lining), Miles Morales si è trasferito ad Harlem insieme alla madre (dopo la morte del padre avvenuta nel capitolo precedente), che nel frattempo si è candidata come sindaco della città. Miles è diventato un subalterno dello Spider-man originale, e spesso lo segue in missione, anche se molti lo ritengono un semplice assistente. Durante uno scontro con Rhino, Peter viene messo KO; Miles invece scopre di avere il potere di generare bioelettricità, abilità che chiamerà Pugno-Venom: con essa sconfiggerà il supercriminale. Più tardi Peter annuncerà a Miles di voler partire per l'Europa insieme a Mary Jane, e gli affiderà il compito di proteggere New York in sua assenza.
Il nuovo Spiderman deve fermare un'invasione alla Roxxon, presa d'assalto da una banda di criminali denominata Underground, i quali intendono fermare i piani della compagnia e del suo presidente Simon Krieger. In seguito, durante un party di Natale, Miles reincontra Phin Mason, un tempo sua amica, con la quale aveva interrotto i rapporti da oltre un anno. Il giorno dopo, il suo amico Ganke crea un nuovo costume e una app tramite la quale Spider-man può ricevere richieste d'aiuto: il primo a chiedere aiuto è lo zio paterno di Miles, Aaron Davis, il quale lascia intendere di conoscere la sua vera identità. Durante un comizio di Rio, Harlem viene attaccata dagli Underground, i quali intendono rubare il Nuform, un potentissimo materiale che genera energia. Miles si scontra anche col loro capo, il supercriminale Tinkerer: la loro lotta furibonda provoca il crollo del Robert F. Kennedy Bridge. Addolorato per aver messo in pericolo i cittadini e per essersi lasciato scappare Tinkerer, Miles è tentato di lasciare la carriera di Spiderman, ma viene dissuaso da Ganke: i due elaborano così un terzo costume in grado di sfruttare al massimo i suoi poteri, compreso quello appena scoperto dell'invisibilità.
Spiderman viene in seguito contattato da Prowler, un ex-criminale ora al servizio della Roxxon, dietro la cui identità si cela lo zio Aaron. Col suo aiuto, Miles scopre che Tinkerer è in realtà Phin, la quale vuole vendicare la morte di suo fratello Rick: questi aveva scoperto che il Nuform ha degli effetti collaterali letali, ed era stato ucciso da Krieger dopo aver tentato di sabotare il progetto. Phin vuole quindi rubare tutto il Nuform prodotto e poi utilizzarlo per distruggere la Roxxon.
Aaron convince Miles a sfruttare la fiducia di Phin per infiltrarsi negli Underground. Il ragazzo ci riesce ma viene scoperto ed è costretto a rivelarle la sua identità. Mentre cerca di dissuaderla dai suoi propositi, i due vengono rapiti da Prowler, che li porta alla Roxxon. Qui Aaron confessa di aver spiato Phin per tramite di Miles, e che Simon Krieger ha modificato il reattore nucleare del Nuform in modo che, se il piano di Phin avesse successo, l'intero Harlem andrebbe distrutto. I due devono quindi combattere contro Rhino, il quale indossa un'amatura potenziata e resistente al Pugno-Venom: una volta sconfitto, il supercriminale provoca Phin ricordandole la morte di Rick. La ragazza, furibonda, sta per ucciderlo, ma Miles glielo impedisce: i due lottano e Phin ha la meglio; dopo averlo ferito quasi mortalmente, la ragazza intima a Miles di non tentare mai più di fermarla. Ganke riporta a casa Miles, dove Rio scopre la vera identità di suo figlio e gli dice di non arrendersi. Dopo essersi ripreso, Miles decide di tentare il tutto e per tutto e fermare Phin, ma viene catturato da Prowler, il quale combatte contro di lui nel tentativo di evitare che muoia come suo padre, nel tentativo di compiere il suo dovere. Miles sconfigge suo zio, spiegandogli che non potrà mai sottrarsi alle proprie responsabilità.
Nel frattempo Phin scatena gli Underground nelle strade di Harlem per avere campo libero e compiere il suo piano. Aaron, comprendendo le parole di Miles, lo aiuta a combattere la gang dando a Rio il tempo di evacuare il quartiere; intanto Miles riesce a raggiungere Phin un attimo prima che lei disponga l'ultima dose di Nuform nel reattore. I due combattono furiosamente, mentre il reattore diventa sempre più instabile: pur di salvare Harlem, Miles tenta di assorbirne l'energia, ma non riesce a contenerla tutta. Osservando il suo sacrificio e comprendendo i suoi errori, Phin utilizza i poteri di Tinkerer per portarlo ad alta quota, dove il ragazzo può far esplodere l'energia senza distruggere New York, ma uccidendo lei. Miles precipita al suolo, ferito ma vivo: un piccolo gruppo di cittadini scopre la sua identità, ma promettono di mantenere il segreto e lo riconoscono ufficialmente come lo 'Spiderman di Harlem'.
Il sacrificio di Phin non è vano, perché grazie alle sue azioni e alla testimonianza di Aaron le menzogne di Krieger vengono a galla e l'uomo viene arrestato. Peter ritorna dal suo viaggio e loda le azioni di Miles e lo conforta dicendogli che il suo futuro da Spiderman, pur se difficile, gli darà anche tante gioie. I due Spiderman partono poi per combattere insieme il crimine. Nel frattempo, Norman Osborn ordina al dottor Curtis Connor di sospendere gli esperimenti su suo figlio Harry, tenuto in uno stato di sospensione comatosa a causa delle sue condizioni instabili.","PlayStation 4 | PlayStation 5 | PC","Avventura stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Marvel's Spider-Man","2018-09-07","Insomniac Games","imgs/locandina Marvel's SpiderMan.jpg","","Peter Parker, alias Spider-Man, con l'aiuto dell'agente di polizia Yuri Watanabe riesce a far arrestare il criminale Wilson Fisk, il quale, ora che la polizia ha abbastanza prove riguardo ai crimini e alle azioni illecite da lui commesse, viene arrestato e rinchiuso nel Raft, un carcere di massima sicurezza dove sono rinchiusi altri nemici dell'Arrampicamuri, tra cui Max Dillon / Electro, Adrian Toomes / Avvoltoio, MacDonald 'Mac' Gargan / Scorpion e Aleksei Sytsevich / Rhino. In seguito all'arresto di Fisk, Peter inizia a indagare su un'irruzione in una casa d'aste dove sono conservate alcune opere d'arte appartenenti a Fisk; qui incontra la sua ex ragazza Mary Jane Watson, anche lei infiltratasi per denunciare pubblicamente le azioni di Fisk. Improvvisamente l'edificio viene attaccato da alcuni uomini armati e con addosso delle mostruose maschere bianche e nere, intenti a recuperare un misterioso file; Mary Jane, che aveva trovato il file prima di loro, scopre che esso riguarda un misterioso oggetto chiamato 'il Respiro del Diavolo'.
Gli sgherri mascherati la sorprendono e cercano di attaccarla, ma Spider-Man interviene e la salva allontanando i criminali, che però riescono a fuggire con il file. Nonostante i loro rapporti distaccati, Peter e Mary Jane decidono di indagare insieme sul furto alla casa d'aste e il ragazzo, che aveva trovato una delle maschere alla casa, si rivolge a Martin Li (il direttore della F.E.A.S.T., un rifugio per i senzatetto dove lavora anche May Parker, zia di Peter) per scoprire l'origine e il significato dell'artefatto: Martin rivela che la maschera ha a che fare con un'organizzazione criminale nota come 'i Demoni' e consiglia a Peter di non interferire con i loro piani. Peter ritorna al laboratorio del dottor Octavius per aiutarlo nella realizzazione di un braccio artificiale, ma Norman Osborn, il CEO della Oscorp nonché attuale sindaco di New York, fa chiudere il laboratorio e sospende definitivamente le ricerche di Octavius allo scopo di convincere quest'ultimo a lavorare di nuovo per lui, cosa che Otto, che sembra avere un contenzioso aperto con Norman, rifiuta.
Nel frattempo, i Demoni continuano ad attaccare le proprietà di Fisk per poi impossessarsene: Peter riesce a sconfiggerli più volte con l'aiuto dell'ufficiale di polizia Jefferson Davis, che viene lodato dai suoi concittadini per il suo senso di eroismo e viene invitato come ospite d'onore all'evento per la rielezione di Norman Osborn, al quale prendono parte anche la moglie Rio, il figlio Miles, Peter e Mary Jane. Durante la celebrazione, però, Osborn riceve una chiamata anonima il cui mittente minaccia di distruggere sia lui che la sua città; Osborn, spaventato, abbandona la manifestazione e subito dopo i Demoni irrompono e iniziano a seminare il caos, uccidendo alcuni dei presenti tra cui Davis. Peter si accorge tuttavia che il capo dei Demoni è in realtà Martin Li, che ha assunto una forma malefica e corrotta nota come Mister Negative; poco prima di agire, Peter viene messo fuori gioco dai Demoni, che se ne vanno subito dopo.
Nei giorni successivi all'attentato, Miles inizia a lavorare alla F.E.A.S.T. come volontario e nel frattempo fa anche amicizia con Peter, che lo aiuta a superare il lutto; allo stesso tempo, Osborn ingaggia l'abile mercenaria Silver Sable, capo della Sable International (una compagnia militare all'avanguardia), affinché tenga sotto controllo le azioni dei Demoni. Silver mostra tuttavia pochissima fiducia nei confronti di Peter e lo mette spesso in difficoltà nelle sue missioni di eroe. Continuando le sue indagini sui Demoni e Martin Li, il ragazzo scopre che quest'ultimo serba rancore nei confronti di Osborn per la morte dei suoi genitori e che sta cercando di rubare il Respiro del Diavolo, una letale arma biologica creata dalla OSCORP nel tentativo di trovare una cura a malattie gravi.
Martin riesce a rubare il Respiro del Diavolo e cerca di rilasciarlo alla Grand Central Station ma il suo piano viene mandato all'aria da Mary Jane e Peter e viene sconfitto. Nel frattempo Octavius diventa sempre più ossessionato dalla creazione delle sue braccia artificiali e le trasforma in strutture robotiche simili a tentacoli situati sul dorso e controllati da un'interfaccia neurale; Peter, tuttavia, è convinto che l'interfaccia possa avere effetti negativi sulla salute mentale di Octavius, che gli rivela di soffrire di una grave malattia che lo porterà inevitabilmente alla paralisi totale e che le sue nuove braccia gli permetteranno di lavorare quando le sue funzioni motorie cesseranno.
Con le sue nuove braccia, Octavius attacca prima il carcere di Ryker's Island e poi il Raft, provocando un'evasione di massa dei detenuti tra i quali vi sono Martin Li, l'Avvoltoio, Electro, Scorpion e Rhino: i cinque si alleano con Octavius, che ora si fa chiamare Dottor Octopus, formando i Sinistri Sei (Octavius li aveva reclutati in precedenza promettendogli di risolvere i loro problemi in cambio del loro aiuto). Essi riescono a sconfiggere facilmente Spider-Man, avvisandolo poi di non interferire con i loro piani. In seguito, Octavius riesce a rubare il Respiro del Diavolo e lo rilascia in mezzo a Times Square, infettando migliaia di civili, compresa Zia May: con l'evasione di massa, New York è invasa dai detenuti e sprofonda letteralmente nel caos, mentre Osborn dichiara una legge marziale e Spider-Man, considerato il responsabile dell'evasione, viene etichettato come un fuggitivo. Fortunatamente, Peter riesce a sconfiggere prima l'Avvoltoio ed Electro e in seguito anche Rhino e Scorpion, mentre Mary Jane scopre che la Oscorp sta apparentemente lavorando ad un antidoto per il Respiro Del Diavolo. Infiltratasi nell'attico di Osborn, MJ scopre che Harry Osborn, il figlio di Norman, è in fase terminale di una gravissima malattia ereditaria di cui il Respiro del Diavolo doveva essere la cura. Entrando nel laboratorio segreto di Osborn, scopre poi che Martin Li è stato una delle 'cavie' per testare gli effetti del Respiro del Diavolo in quanto Norman cercava, all'epoca, una cura per sua moglie, affetta dalla stessa malattia del figlio. Grazie all'esperimento Martin ottenne i suoi poteri ma, a causa di essi, uccise inavvertitamente i suoi genitori; questo spiega il suo odio nei confronti di Osborn e anche il motivo per cui Octavius cessò di collaborare con lui alla OSCORP. Mary Jane riesce a scoprire l'ubicazione dell'antidoto, che è tenuto in uno dei tanti laboratori della OSCORP in città, e lo riferisce a Peter: dopo aver ottenuto la fiducia di Silver, Peter entra nel laboratorio e combatte Li, sconfiggendolo definitivamente e trovando l'antidoto. In quell'istante giunge Octavius, che mette fuori gioco Peter, rapisce Osborn e si porta via l'antidoto.
Il ragazzo costruisce quindi un nuovo costume potenziato e raggiunge Octavius in cima alla torre della OSCORP, impedendogli di uccidere Osborn; dopo un lungo scontro, Octavius rivela a Peter di aver sempre saputo che lui e Spider-Man sono la stessa persona: il ragazzo, preso dall'ira, sconfigge Octavius danneggiando l'interfaccia neurale con la quale controllava le braccia robotiche. Octavius, ormai sconfitto e incapace di muoversi, chiede a Peter di perdonarlo e di continuare a lavorare con lui, altrimenti potrebbe rivelare la sua identità, ma il ragazzo, nonostante il dispiacere e il timore di essere smascherato, rifiuta e se ne va con l'antidoto. Giunto alla F.E.A.S.T., Peter scopre che la quantità di antidoto non è sufficiente a curare tutti i pazienti, trovandosi così di fronte a una dura decisione: o usare tutto l'antidoto per curare Zia May (ormai in fin di vita) oppure lasciare che i medici ne producano abbastanza per curare tutti. Peter, a malincuore, sceglie la seconda possibilità lasciando morire Zia May, la quale, prima di morire, gli rivela di aver sempre saputo della sua doppia identità e di essere orgogliosa di lui. Tre mesi dopo, Osborn abbandona la sua campagna elettorale essendo caduto in disgrazia, la Sable International lascia New York e Peter, seppur addolorato per la morte di Zia May, riprende la sua relazione con Mary Jane.
In una scena a metà dei titoli di coda, Miles rivela a Peter di aver acquisito poteri da ragno dopo essere stato morso da un esemplare geneticamente modificato che si era liberato dai laboratori di Osborn quando Mary Jane si era infiltrata nel suo attico e Peter, date le circostanze, rivela a Miles la sua duplice identità. In un'altra scena alla fine dei titoli di coda, Osborn entra nel suo laboratorio segreto e osserva la camera di isolamento nella quale è rinchiuso suo figlio Harry, in coma e parzialmente ricoperto da una strana sostanza nera simile ad una ragnatela. Osborn giura al figlio di trovare una cura per la sua malattia, mentre la misteriosa sostanza prende la forma di una mano, lasciando intendere che si tratti di un Simbionte.","PlayStation 4 | PlayStation 5 | PC","Avventura stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("The Last of Us Parte 1","2013-06-14","Naughty Dog","imgs/locandina The Last of Us Parte 1.jpg","","Nel 2013 un'epidemia generata dal fungo Cordyceps mutato si scatena negli Stati Uniti d'America, trasformando gli esseri umani in creature aggressive. Nei sobborghi di Austin Joel Miller sfugge al caos insieme a suo fratello Tommy e alla figlia tredicenne Sarah. Tuttavia, durante la fuga, Sarah viene colpita da un soldato e muore tra le braccia di Joel, che rimarrà inevitabilmente e profondamente segnato dall’accaduto.
Vent'anni dopo, nel 2033, la civiltà è stata decimata dall'infezione. I sopravvissuti risiedono in zone di quarantena sotto stretta sorveglianza, in insediamenti indipendenti o in gruppi nomadi. Joel lavora come contrabbandiere insieme a Tess, la sua compagna in affari, nella zona di quarantena di Boston, nel Massachusetts. I due danno la caccia a Robert, un trafficante del mercato nero, nel tentativo di recuperare le loro armi. Prima che Tess lo uccida, l'uomo le rivela di aver venduto il carico alle Luci, una milizia ribelle che si oppone alle autorità militari.
La leader delle Luci, Marlene, promette di restituire loro il doppio del carico se porteranno Ellie, una ragazzina quattordicenne, al palazzo del governo fuori dalla zona di quarantena. I tre fuggono dalla zona di notte, ma dopo essersi imbattuti in una pattuglia militare, scoprono che Ellie è infetta. In casi normali, l'individuo infettato si trasforma dopo due giorni, ma Ellie afferma di essere stata morsa tre settimane prima e che la sua immunità potrebbe portare alla creazione di un vaccino. Dopo aver raggiunto l'edificio superando orde di infetti, si rendono conto che le Luci presenti sul luogo sono state uccise. Tess rivela di essere stata contagiata e, credendo nell'importanza di Ellie, decide di sacrificarsi venendo uccisa dai soldati che li inseguivano per permettere a Joel ed Ellie di fuggire. Joel decide quindi di andare a trovare Tommy, ex-membro delle Luci, nella speranza di localizzare le Luci rimaste.
Con l'aiuto di Bill, un trafficante e sopravvissuto che deve a Joel un favore, i due riescono a recuperare un'auto ma mentre attraversano Pittsburgh, vengono assaliti da alcuni banditi e l'auto viene distrutta. Obbligati a proseguire a piedi, si alleano con due fratelli, Henry e Sam, nel tentativo di fuggire dalla città. Dopo esserci riusciti, tuttavia, Sam viene morso, trasformandosi e attaccando Ellie, venendo però ucciso da Henry, il quale si suicida poco dopo per la disperazione. In autunno, Joel ed Ellie trovano finalmente Tommy a Jackson, dove l'uomo ha costruito un insediamento vicino a un impianto idroelettrico con sua moglie, Maria. Joel decide quindi di lasciare Ellie con Tommy perché possa portarla dalle Luci, ma dopo una discussione fra i due, nella quale Ellie rivela di essere stata informata su Sarah, decide di tenerla con sé. Tommy li indirizza quindi al laboratorio delle Luci nell'università del Colorado. Giunti sul posto, scoprono che la milizia si è trasferita nell'ospedale di Salt Lake City, nello Utah. I due vengono quindi attaccati da dei banditi e Joel rimane ferito gravemente dopo essere trafitto da un tondino di ferro.
Durante l'inverno, Ellie e Joel si riparano sulle montagne. Le condizioni dell'uomo peggiorano, ma Ellie si prende cura di lui. Mentre è a caccia, la ragazza incontra David e James, due cacciatori disposti a scambiare delle medicine in loro possesso in cambio di cibo. Dopo aver seguito Ellie al suo nascondiglio, e dove la ragazza riesce a somministrare le medicine a Joel, i due uomini si rivelano ostili e spiegano che i banditi presenti all'università erano parte del loro gruppo. Ellie viene catturata da David, che ha intenzione di reclutarla all'interno della sua banda, la quale si rivelerà poi essere composta da cannibali. Dopo essersi rifiutata di unirsi a loro, Ellie uccide James e fugge, venendo però messa all'angolo da David all'interno di un ristorante in fiamme. Nel frattempo, Joel si riprende dalle sue ferite e parte per cercare Ellie, trovandola proprio mentre, disperata, massacra David con un machete, giungendo in tempo per tranquillizzarla e prenderla con sé.
In primavera, Joel ed Ellie arrivano a Salt Lake City. Ellie perde i sensi dopo essere quasi annegata nelle gallerie allagate della metropolitana ed entrambi vengono catturati da una pattuglia delle Luci. Nell'ospedale, Marlene comunica a Joel che Ellie è già stata sedata e preparata per l'intervento: nella speranza di produrre un vaccino, le Luci rimuoveranno la porzione infetta del cervello della ragazza, causandone il probabile decesso. Deciso a non lasciarla morire, Joel raggiunge la sala operatoria, uccide il chirurgo e porta Ellie ancora sedata nel parcheggio dell'ospedale. Lì uccide Marlene per evitare che le Luci possano seguirli. Lungo la strada del ritorno verso Jackson, dove hanno intenzione di stabilirsi, quando Ellie si sveglia, Joel le racconta che le Luci hanno scoperto la presenza di altre 'decine di persone immuni' e che ormai hanno smesso di cercare una cura, mentendole per non farla sentire in colpa e nascondere la vera ragione del suo atto. Fuori dall'insediamento di Tommy, Ellie chiede a Joel di giurarle che tutto ciò che le ha raccontato è vero. Senza alcuna esitazione, l'uomo risponde con 'lo giuro'.","PlayStation 3 | PlayStation 4 | PlayStation 5","Avventura | Horror | Stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("The Last of Us Parte 2","2020-06-19","Naughty Dog","imgs/locandina The Last of Us Parte 2.jpeg","","In seguito agli eventi del primo capitolo, Joel Miller confessa a suo fratello Tommy di aver impedito alle Luci di effettuare l'intervento che avrebbe trovato una cura per l'epidemia legata al fungo Cordyceps che sta martoriando il mondo, la quale avrebbe però portato alla morte di Ellie. Quattro anni dopo, Joel ed Ellie si sono stabiliti a Jackson, nel Wyoming, sebbene lentamente allontanandosi l'uno dall'altra. Mentre sono di pattuglia, Joel e Tommy salvano una donna sconosciuta di nome Abby Anderson da un'orda di infetti. Il gruppo si rifugia poi ad un avamposto del gruppo di Abby, ex membri delle Luci ora parte della milizia chiamata Washington Liberation Front, la quale ha sede a Seattle. Essi attaccano i due, poiché Abby si scoprirà più avanti essere in cerca di vendetta nei confronti di Joel a causa dell'assassinio di suo padre, il chirurgo delle Luci che tentò di operare Ellie (e che Joel uccise sgozzandolo con un bisturi). Nel frattempo, Ellie e la sua nuova ragazza Dina sono anch'esse di pattuglia e dopo essere state avvertite dall'amico (ed ex-fidanzato di Dina) Jesse del mancato rientro dei fratelli Miller, si dirigono a cercarli. Ellie riesce ad entrare nel rifugio del gruppo ma dopo aver trovato Joel (che sta venendo massacrato da Abby con una mazza da golf) non riesce a salvarlo perché viene bloccata dagli altri ragazzi. Abby dà il colpo di grazia a Joel davanti a Ellie, incapace di liberarsi e costretta a guardare mentre la sua figura paterna viene assassinata. Adirata per l'accaduto, la ragazza giura vendetta nei confronti del gruppo. Tommy ed Ellie vengono risparmiati e si risvegliano dopo l'arrivo di Dina e Jesse.
All'insaputa di tutti, Tommy parte per Seattle per dare la caccia ad Abby e il giorno seguente fanno lo stesso Ellie e Dina. Dopo essere sopravvissuta ad un'imboscata del WLF, Ellie è costretta dopo un incidente a rivelare a Dina di essere immune, con la ragazza che le rivela a sua volta di essere incinta (di Jesse). Il giorno seguente, dopo un flashback in cui la protagonista è con Joel a visitare un museo di scienza abbandonato nel giorno del suo compleanno, Ellie si mette sulle tracce di Tommy da sola e finisce per incontrare Jesse, che ha seguito fino a Seattle le ragazze per aiutarle ma, ferito, non può continuare ad accompagnare la protagonista che si mette alla ricerca di Nora, ragazza appartenente al gruppo di Abby. Nel tentativo di raggiungere l'ospedale in cui si trova Nora, Ellie si imbatte nei Serafiti (soprannominati le Iene), una setta religiosa in lotta con il WLF per il controllo di Seattle. La protagonista riesce poi a raggiungere Nora, uccidendola non prima di averla torturata per scoprire la posizione di Abby, rimanendo molto scossa. Un flashback rivela che, due anni prima, Ellie si era diretta da sola all'ospedale delle Luci di Salt Lake City, scoprendo la verità e decidendo di troncare i rapporti con Joel, perché le aveva mentito su tutto (finale del prequel). Finito il flashback, Ellie si sveglia e viaggia con Jesse per arrivare fino al porto, luogo dove pensa di trovare Tommy. Successivamente Ellie si divide dall'amico perché sicura di trovare Abby all'acquario. La ragazza non si trova nemmeno qui e dopo aver tentato disperatamente di scoprire la sua nuova posizione, Ellie si ritrova costretta ad uccidere anche Owen (ex-fidanzato di Abby) e Mel (fidanzata di Owen). Quest'ultima si rivela essere incinta e ad Ellie viene un attacco di panico, mentre giungono sul luogo Jesse e il ritrovato Tommy, che calmano la ragazza. Tornati al loro rifugio (il teatro) e pronti per tornare a casa, il gruppo viene sorpreso da Abby, che con un colpo di pistola uccide Jesse, prende in ostaggio Tommy e tiene sotto tiro Ellie.
Qui iniziano i tre giorni di permanenza di Ellie a Seattle nei panni di Abby, che si rivela essere a tutti gli effetti una seconda protagonista. Incontriamo nuovi personaggi e vediamo tutto con un altro punto di vista, conoscendo meglio gli amici di Abby (ancora vivi prima dell'arrivo di Ellie) e una storia completamente differente da quella dell'altra protagonista. Owen è sparito mentre indagava sull'attività delle Iene. Dirigendosi di nascosto a cercarlo, Abby viene però catturata dai Serafiti. Quando sta per morire dopo essere stata impiccata da una serafita di nome Emily, viene salvata da Yara e Lev, due fratelli appartenenti alla setta ma considerati apostati dopo che il secondo si era rifiutato di accettare i loro ideali. Sebbene Yara sia gravemente ferita ad un braccio, Abby decide di lasciare i due per andare a cercare Owen, il quale ha intenzione di salpare per Santa Barbara, dove le Luci sembrano starsi riformando. Dopo aver passato la notte con Owen, Abby torna indietro per salvare Yara e Lev e li porta da Owen e Mel, talentuosa chirurga. Dopo un tortuoso viaggio, Abby raggiunge insieme a Lev l'ospedale del WLF dove raccoglie, con l'aiuto di Nora, gli strumenti necessari per permettere a Mel di amputare il braccio di Yara. In seguito all'intervento, Lev fugge verso la 'base' dei Serafiti (un'isola controllata interamente dalla setta) per cercare di convincere sua madre ad abbandonare le Iene e unirsi a loro, vedendosi però costretto ad ucciderla per autodifesa, mentre viene inseguito da Abby e Yara. I tre fuggono proprio mentre il WLF assalta l'isola della setta e per salvare il ragazzino a cui ormai si è molto legata, Abby tradisce la milizia, mentre Yara si sacrifica per permettere ai due di fuggire uccidendo Isaac, il leader del WLF. La coppia ritorna poi all'acquario e trova i cadaveri di Owen e Mel, insieme ad una mappa lasciata erroneamente da Ellie che conduce al loro nascondiglio. Tornati nel presente alla scena del teatro, Tommy cerca di liberarsi da Abby ma quest'ultima gli spara ferendolo e privandolo della vista da un occhio. Ellie guadagna tempo e prova a fuggire, ma viene inseguita da Abby. Quest'ultima affronta Ellie e Dina (nel frattempo accorsa in aiuto), riuscendo a sopraffarle entrambe. Tuttavia, convinta da Lev, Abby decide di risparmiarle, intimando loro di non farsi rivedere mai più.
Un anno dopo, Ellie e Dina vivono in una fattoria e crescono insieme il figlio di Dina e Jesse (che hanno chiamato JJ) sebbene Ellie soffra ancora di disturbo da stress post-traumatico in seguito alla morte di Joel e agli eventi di Seattle. Quando Tommy giunge alla fattoria rivelando di aver localizzato Abby, Ellie non riesce a rifiutare e parte per farla finita una volta per tutte, nonostante Dina la implori a restare. Nel frattempo, Abby e Lev si trovano a Santa Barbara in cerca delle Luci, le quali si stanno effettivamente raggruppando sull'isola di Santa Catalina. I due vengono però catturati, torturati e lasciati a morire (legati a dei pali in spiaggia) dal gruppo di predoni schiavisti noto come Serpi. Dopo averla raggiunta, Ellie (anch'essa gravemente ferita dopo l'incontro con le Serpi) slega i due e sembra volerli lasciare andare. Dopo aver ricordato ancora una volta il volto martoriato di Joel, Ellie obbliga Abby ad affrontarla in uno scontro corpo a corpo. Ellie perde persino due dita ma alla fine riesce a sopraffare Abby. Quando è sul punto di ucciderla affogandola, decide però di lasciarla andare, dopo aver ricordato per la prima volta il volto di Joel sereno. Abby e Lev salpano quindi verso l'isola di Santa Catalina, dove potranno unirsi alle Luci. Dopo un salto temporale vediamo Ellie tornata alla fattoria, ormai abbandonata e senza nessuna traccia di Dina e JJ. Sono rimaste soltanto le cose della protagonista in una stanza al piano di sopra. Qui, Ellie, provando senza successo (per via della perdita delle due dita) a suonare la vecchia chitarra regalata da Joel, ricorda l'ultima conversazione con lui (la sera prima della sua morte) durante la quale gli disse che avrebbe dopo tanto tempo provato a perdonarlo. Finito il ricordo, la ragazza posa la chitarra, ultimo simbolo del legame tra lei e l'uomo, e parte da sola verso un futuro incerto.","PlayStation 4 | PlayStation 5","Avventura | Horror | Stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Uncharted: Drake's Fortune","2007-12-07","Naughty Dog","imgs/locandina Uncharted 1.jpg","","Al largo delle coste di Panama, Nathan 'Nate' Drake recupera dal fondo dell'oceano la bara dell'esploratore e corsaro inglese, nonché suo antenato, Sir Francis Drake. L'evento è ripreso dalla giornalista Elena Fisher, la cui compagnia ha finanziato l'impresa per avere un buon documentario. Nella bara però non sono presenti le spoglie di Sir Francis, ma solo il suo diario (la cui ultima pagina è stata strappata) contenente la locazione di El Dorado. In quel momento, i due vengono attaccati da dei pirati, ma in loro soccorso arriva Victor Sullivan detto 'Sully', amico di Nathan.
Liberatisi della giornalista, Nathan e Sully si recano in Colombia, dove secondo le informazioni contenute nel diario dovrebbe trovarsi El Dorado. Risalendo il Rio delle Amazzoni trovano un antico tempio Inca e scoprono che El Dorado non è una città, ma un'enorme statua d'oro, che però fu rimossa circa 400 anni prima del loro arrivo dai Conquistadores.
Seguendo le tracce sugli spostamenti della statua, il duo scopre un sottomarino tedesco risalente all'epoca nazista, incagliato da tempo nel Rio delle Amazzoni. Mentre Sully lo aspetta fuori, Nathan si addentra nel sommergibile, e al suo interno, trova la parte mancante del diario di Drake, per l'esattezza una mappa che rivela l'esistenza di un'isola nell'Oceano Pacifico dove la statua fu probabilmente portata. Uscito dal sommergibile, Nathan scopre Sully ostaggio di Gabriel Roman, un cacciatore di tesori che si avvale di mercenari tra i quali Atoq Navarro, un archeologo che conosce la leggenda della statua. Nonostante Nathan consegni la mappa appena trovata a Gabriel, questo spara comunque a Sully in petto. Nate è costretto a fuggire ma, mentre sta scappando, ritrova Elena, adirata per il fatto che l'abbiano abbandonata. Il duo rapidamente sale sull'aereo di Sully e si dirige verso l'isola dove si troverebbe la statua.
In prossimità dell'isola, il loro aereo viene colpito: per salvarsi si lanciano col paracadute, ma si dividono. Nathan si mette in cerca della ragazza. Esplorando l'isola avvista il paracadute di Elena, impigliato in un vecchio forte spagnolo, e decide di recarcisi, scoprendo che probabilmente la statua è arrivata sull'isola presso una colonia spagnola poco distante. Durante il tragitto viene catturato da Eddy Raja, un suo vecchio rivale ora dedito alla pirateria. Poiché è alleato di Roman, Eddy cerca di obbligarlo ad aiutarlo nella ricerca di El Dorado ma Nate viene liberato da Elena che, con una jeep, sradica la parete della prigione in cui si trovava. Fuggono insieme, inseguiti nella giungla, e si dirigono presso i resti della colonia spagnola: una città abbandonata e semi-sommersa dall'acqua. Mentre cercano il tesoro, le cose si complicano con l'arrivo sull'isola di Gabriel, Navarro e i loro mercenari, insieme a un redivivo Sully. Questi si inoltrano verso l'entroterra nella direzione di un vecchio monastero, quindi Nate ed Elena decidono di andare a recuperare Sully.
I due risalgono così il fiume e, giunti al monastero, si riuniscono a Sully che, sopravvissuto miracolosamente, finge di aiutare Gabriel e i suoi uomini fornendo loro false piste, mentre in verità ha trovato reali prove che il tesoro è stato portato fin lì. Nathan ed Elena esplorano le catacombe sotto al monastero dove si troverebbe la statua e lì sotto trovano lo scheletro di Francis Drake, lasciando Nathan scoraggiato poiché ritiene che l'antenato ha perso la vita senza neanche trovare il tesoro. In quel momento si imbattono in Eddy che fugge inseguito da esseri umani, mutati e vagamente simili a zombie, e dotati di incredibile forza e velocità. Eddy perisce sotto i loro attacchi, mentre Nathan ed Elena riescono a scappare e si ritrovano in una vecchia base tedesca costruita nell'isola durante la seconda guerra mondiale, poiché anche i tedeschi avevano provato a cercare El Dorado. Per proseguire serve ridare energia al bunker, quindi Nathan si avventura da solo nel complesso sfidando ancora gli esseri umani mutati. Dopo aver messo in moto il generatore, Nathan trova un filmato in cui vede come i tedeschi scoprirono la statua, ma non riuscirono a capire in tempo che in essa si celava quella che appariva essere una terribile maledizione, la quale aveva trasformato i soldati esposti in mutanti. Scopre anche, attraverso una lettera, come Francis Drake molto tempo prima sacrificò la sua vita e distrusse la colonia per impedire che la statua lasciasse l'isola, evitando la distruzione del mondo con i suoi effetti.
Nathan si appresta a tornare da Elena quando scopre che è stata catturata da Gabriel e Navarro, quindi affronta sia i mercenari di Gabriel sia i mutanti. Riesce a tornare da Sully e insieme si dirigono nel punto in cui è nascosto El Dorado per impedire che lasci l'isola. Tuttavia, arrivati alla statua, scoprono che è già stata trovata da Gabriel e dai suoi uomini. Navarro suggerisce a Gabriel di aprirla per trovarvi il 'vero tesoro', così facendo scopre che la statua d'oro in realtà altro non è che un sarcofago contenente una mummia che rilascia delle spore. Dopo averle inalate, Gabriel si trasforma anche lui in un mutante, aggressivo e fuori controllo, ma viene subito ucciso da Navarro. Si scopre così che quest'ultimo conosceva la leggenda e il segreto di El Dorado fin dall'inizio, e che era alla ricerca del tesoro non per l'oro, bensì per il potere mutante delle spore da impiegare sulle persone per poi usarle come armi biologiche. In quel momento arrivano sul posto i mutanti, che fanno fuori tutti i mercenari presenti.
Tenendo ancora in ostaggio Elena, Navarro usa un elicottero per recuperare la statua, ma Nathan riesce a saltare al volo sul velivolo mentre Sully si occupa di eliminare i mutanti. L'elicottero però si schianta sul ponte della nave di Navarro. Durante la battaglia finale, Nathan riesce a liberarsi della statua buttandola in acqua e trascinando anche Navarro sul fondo del mare. Nathan infine salva Elena e, sul punto di baciarsi, vengono interrotti da Sully, che è riuscito a rubare una barca con un sostanzioso bottino d'oro per tutti e tre. Quando lasciano l'isola, Elena ricorda a Nathan che gli deve ancora una storia per il suo show e Nathan le promette che l'avrà.","PlayStation 3","Avventura dinamica");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Uncharted 2: Il Covo dei Ladri","2009-10-14","Naughty Dog","imgs/locandina Uncharted 2.jpg","","Due anni dopo la scoperta di El Dorado, Nathan Drake rincontra un suo vecchio amico, Harry Flynn e la sua ragazza (e vecchia fiamma di Nate) Chloe Frazer. Gli raccontano che il cliente di Flynn, Zoran Lazarevic, vuole che recuperi una lampada ad olio da un museo. Nate all'inizio rifiuta, ma accetta quando scopre che potrebbe celare la risposta al destino delle navi perdute di Marco Polo. Inoltre Nate e Chloe pianificano di scappare insieme dopo aver rinvenuto il tesoro all'insaputa di Flynn.
Ad Istanbul, Nate e Flynn irrompono nel museo e trovano la lampada. Al suo interno, trovano una pergamena completamente nera e della resina. Nate brucia la resina, la cui luce rivela sulla pergamena una mappa e l'esistenza di lettere apocrife di Polo. Scopre che la flotta venne colpita da uno tsunami al largo della costa occidentale del Borneo e che trasportava la pietra Cintamani da Shambhala. Nel momento in cui si preparano ad andarsene, Flynn tradisce Nate facendo scattare l'allarme e lasciandolo in balia delle guardie di sicurezza del museo. Nate tenta di scappare ma viene catturato dalla polizia turca.
Dopo tre mesi di galera, Nate riceve la visita del suo vecchio amico Victor 'Sully' Sullivan, dicendogli di aver pagato la cauzione. Insieme a Sully c'è anche Chloe, che decide di unirsi con Nate alla ricerca della flotta di Polo e della pietra visto che Lazarevic ha trovato la flotta perduta.
In Borneo, Nate, Chloe e Sully trovano l'accampamento di Lazarevic. Nate scopre, attraverso le lettere di Marco Polo trovate da Lazarevic, che gli uomini sfuggirono allo tsunami sulla montagna vicina. Il trio raggiunge l'ultimo rifugio dell'equipaggio di Polo in un antico tempio e lì scoprono che tutti sono ridotti a scheletri con i denti neri. Nate accende della resina e segue delle vecchie tracce di sangue fino ad una scatola contenente un Phurba ed un'altra pergamena di Polo che svela un ulteriore indizio riguardo ad un tempio nella città di Kathmandu in Nepal. Sopraggiungono gli uomini di Flynn: Chloe decide di restare sotto copertura e fare finta di aver scovato i suoi amici. Flynn prende la mappa ed ordina a due soldati di condurre Nate e Sully da Lazarevic. Lontana dagli occhi di Flynn, Chloe uccide le due guardie e dà a Nate il Phurba dicendogli di incontrarsi in Nepal. Sully e Nate riescono a scappare e, una volta al sicuro, Sully informa Nate che non lo accompagnerà in Nepal, sentendosi 'troppo vecchio per queste cose'.
In Nepal, Nate trova la città divisa da una guerra civile e dalle truppe di Lazarevic, anche loro alla ricerca del tempio. Nate ritrova Chloe e i due si mettono alla ricerca del tempio, che ha dei simboli uguali a quelli sul Phurba. Mentre girano in una delle vie della città, incontrano la giornalista Elena Fisher ed il suo cameraman Jeff che stanno cercando Lazarevic nella speranza di avere una buona storia. Nate propone ad Elena e Jeff di venire con loro nonostante Chloe non sia concorde. Si inoltrano nel tempio e scoprono che la pietra e Shambhala sono nelle montagne dell'Himalaya. Nel frattempo, Jeff viene colpito da uno degli uomini di Lazarevic e Nate, Elena e Chloe cercano di portarlo al sicuro. Il gruppo però viene trovato da Flynn e Lazarevic: quest'ultimo uccide Jeff, prende il Phurba e costringe Chloe a seguirli sul loro treno. Flynn viene lasciato indietro ad uccidere Nate ed Elena, che però riescono a scappare.
Nate decide di andare al deposito treni per salvare Chloe ed Elena decide di aiutarlo. Grazie ad una jeep rubata dalla ragazza, Elena aiuta Nate a raggiungere il treno riuscendo a saltare appena in tempo sull'ultima carrozza. Nate quindi si dirige verso il primo vagone: ritrova Chloe che gli intima di andarsene ma viene colpito allo stomaco da Flynn. Nate è costretto a sparare a dei contenitori di gas propano uccidendo gli uomini di Flynn, ma mandando se stesso e metà del treno fuori dai binari in bilico su un burrone. Ferito e malconcio, riesce a mettersi in salvo recuperando anche il pugnale Phurba, ma successivamente Nate sviene per lo sfinimento.
Si sveglia in un villaggio tibetano dopo essere stato salvato da uno degli uomini del villaggio di nome Tenzin. L'uomo lo porta da Elena (che aveva seguito le tracce dell'incidente) e al capo del villaggio, un vecchio tedesco di nome Karl Schafer. Schafer dice a Nate che il Phurba è la chiave per Shambala, e che se Lazarevic riuscirà a prendere la pietra nessuno potrà fermarlo. Quindi manda Nate e Tenzin a cercare i resti della sua spedizione in cerca di Shambala, condotta dallo stesso Schafer settant'anni prima. Nelle caverne di ghiaccio, Nate e Tenzin scoprono che gli uomini di Schafer erano SS che facevano parte dell'Ahnenerbe e che furono uccisi da Schafer per proteggere il mondo dal potere della pietra. Mentre sono ancora nelle caverne, il duo viene attacco da strani e spaventosi mostri simili a Yeti, dotati di forza e agilità sovrumane. Alla loro fuga, scoprono che Lazarevic ha attaccato il villaggio di Tenzin e rapito Schafer. Nate e Tenzin sconfiggono gli uomini di Lazarevic e salvano il villaggio dalla distruzione totale. Nate ed Elena quindi vanno alla ricerca di Schafer.
Dopo aver distrutto metà del convoglio di Lazarevic, il camion di Nate ed Elena viene mandato giù da un precipizio; riescono tuttavia a salvarsi e a raggiungere un antico monastero buddhista che nasconde l'entrata segreta per Shambhala. La coppia combatte attraverso i molti edifici del monastero in cerca di Schafer; quando lo trovano è in punto di morte a causa delle percosse che Lazarevic gli ha inflitto per le informazioni. Prima di morire, Schafer dice a Nate che deve distruggere la pietra Cintamani, e di credergli.
Nate ed Elena vanno alla ricerca del Phurba mentre gli uomini di Lazarevic vengono continuamente attaccati e uccisi dagli stessi mostri che hanno attaccato Nate e Tenzin nelle caverne di ghiaccio. Trovano il pugnale da Chloe che glielo dà solo con la promessa di uccidere Lazarevic. Nate ed Elena trovano il passaggio segreto per Shambala, ma arriva Lazarevic che minaccia Nate di uccidere sia Elena che Chloe se non apre le porte ma, prima che Nate possa farlo, arrivano i mostri che cercano di ucciderli, ma Lazarevic li uccide a sua volta e scopre che i 'mostri' in realtà sono uomini mutati dalla pelle viola e i denti neri che indossano costumi mostruosi, ovvero i Guardiani di Shambala. Arrivati in città, vengono attaccati di nuovo dai Guardiani, armati di balestre e non più travestiti, che iniziano uno scontro con i soldati. Questo permette a Nate, Elena e Chloe di scappare. Il trio raggiunge il tempio nel centro della città contenente la pietra, ma una volta lì scoprono che Polo si sbagliava, che la pietra non era uno zaffiro gigante, ma un grande frammento di ambra color azzurro, creata con la resina dell'Albero della vita.
Scoprono così che Lazarevic si sta recando proprio all'Albero. Ma prima di raggiungerlo, compare Flynn ferito, con una granata con la spoletta mancante. Flynn fa cadere la bomba, uccidendo se stesso e ferendo gravemente Elena. Chloe e Nate la aiutano a raggiungere l'entrata; una volta lì, mentre Chloe porta Elena il più lontano possibile, Nate si dirige da solo a fermare Lazarevic. Nate lo trova alla base dell'Albero della vita a bere la resina, capace di curare le ferite e conferire resistenza e forza sovrumane: Nate lo affronta in combattimento e lo sconfigge. Decide però di non ucciderlo e, allontanandosi dallo scontro, lascia Lazarevic ai Guardiani, che iniziano a picchiarlo fino alla morte.
Nate, Chloe ed Elena scappano dalla città di Shambhala che si sta sgretolando a causa della distruzione dell'Albero dovuta al combattimento con Lazarevic. Tornati al villaggio tibetano, Chloe chiede a Nate se amasse davvero Elena. Rendendosi conto di amarla, Chloe gli dice che dovrebbe dirglielo; i due si dicono addio quando compare Sully che sorregge Elena guarita. La ragazza gli chiede dove andranno ora e Nate, insicuro dei suoi piani futuri, la bacia; quindi insieme guardano il sole tramontare dietro le montagne.","PlayStation 3","Avventura dinamica");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Uncharted 3: L'Inganno di Drake","2011-11-02","Naughty Dog","imgs/locandina Uncharted 3.jpg","","In un pub di Londra, Nathan Drake e il suo mentore Victor Sullivan hanno intenzione di vendere l'anello di Sir Francis Drake a un uomo di nome Talbot. Tuttavia, l'affare va a monte poiché i due scoprono che il denaro è falso e Talbot, per tutta risposta, sguinzaglia i suoi uomini. Scappando, vengono fermati e uccisi da Charlie Cutter, un buttafuori al servizio della perfida Katherine Marlowe, che sottrae l'anello a Drake e fugge insieme a Talbot.
In un flashback, vent'anni prima, a Cartagena, in Colombia, un giovane Nathan è impegnato nel furto in un museo dell'anello di Sir Francis e di un particolare astrolabio, invenzione del medesimo, dall'uso sconosciuto. Nate riesce a rubare l'anello ma è costretto a cedere l'astrolabio a Katherine Marlowe, che è accompagnata da un uomo, rivelatosi essere Victor Sullivan. Nathan cerca di scappare e viene aiutato nella fuga da Sully. Rimasto colpito dalle abilità di ladro di Nathan, l'uomo prende sotto la sua ala il giovane ragazzo, cominciando cosi la loro alleanza per il recupero dell'astrolabio.
Ritornando al presente, l'omicidio di Nate e Sully si rivela essere un bluff di Cutter, in realtà un loro alleato. I tre si incontrano con la collega Chloe Frazer, che li informa della destinazione di Marlowe. Inseguendo la donna per i sotterranei di Londra, il gruppo giunge al covo di un misterioso ordine segreto votato alla ricerca dell'anello di Drake, che affonda le sue radici nella Londra di 400 anni prima, guidato proprio dagli antenati di Marlowe. Nate recupera l'astrolabio, in realtà un decodificatore di caratteri sabei in latino, che viene attivato dall'inserimento dell'anello nello stesso; Nate ne deduce che Sir Francis avesse scoperto la posizione della mitica città di Iram dei Pilastri, e, soprattutto, di come l'archeologo T.E. Lawrence avesse una parte degli indizi per arrivare ad essa. I tre fuggono dal covo e decidono di dividersi, seguendo le due diverse piste trovate da Lawrence: Nate e Sully si dirigono in un castello in Francia, Chloe e Charlie vanno in una cittadella in Siria. I primi trovano la metà di un amuleto all'interno di una cripta ma vengono attaccati da Talbot, che ruba il manufatto e dà fuoco al castello. Nate e Sully fuggono per dirigersi in Siria, dove temono che Chloe e Cutter siano stati vittime di un simile agguato.
Si incontrano con il resto del gruppo, che ha scoperto che Marlowe cerca di ottenere il particolare potere di sfruttare le paure dei propri nemici. Trovata l'altra metà dell'amuleto, essa rivela che la posizione del prossimo indizio è in Yemen. Tuttavia, Cutter viene accerchiato e, oltre a perdere il pezzo di amuleto, si rompe una gamba cercando di fuggire. Il gruppo riesce a scappare, ma Chloe e Cutter si ritirano, mentre Nate e Sully iniziano il viaggio verso lo Yemen.
Per arrivare a destinazione senza subire controlli, il duo chiede aiuto a Elena Fisher, che fornisce loro dei pass da giornalisti. Elena sospetta che il loro arrivo nello Yemen non sia solo parte di una semplice ricerca storica ma di un'ennesima caccia a qualche tesoro misterioso senza aver tenuto conto dei propri rischi e, soprattutto, di quelli di Sully. Inoltre, Nate ed Elena hanno questioni sentimentali irrisolte: la ragazza porta ancora un anello al dito, evidentemente regalatole da lui, ma si giustifica fornendo l'utilità di essere considerata una donna sposata in un paese arabico. Elena aiuta il duo ad orientarsi nel paese ed insieme localizzano un percorso sotterraneo, dove scoprono una mappa raffigurante delle costellazioni, e memorizzata da Sully, grazie alla quale potranno trovare la città perduta di Ubar nel deserto del Rub' al-Khali. Lì era diretto anche Francis Drake ma, per qualche causa misteriosa, abbandonò la stessa missione. Usciti dai sotterranei, Nate viene colpito da un dardo contenente allucinogeni.
Nate si sveglia braccato da Marlowe, Talbot e un pirata di nome Ramses. Marlowe spiega a Nate il perché hanno bisogno di lui e delle sue capacità da cacciatore di tesori, e anche di Sullivan dal momento che conosce l'ubicazione di Ubar. Ma quando Talbot avvisa Marlowe di aver individuato Sully ed è sul punto di catturarlo, Nate riesce a scappare ed inseguire Talbot per tutta la città. Nate però viene stordito alle spalle da Ramses, che lo porta sulla sua nave. Nate riesce ad abbattere gli scagnozzi del pirata e si assicura che Sully non sia sulla nave. Ferito a morte, Ramses provoca un buco allo scafo della nave facendola affondare. Nate scappa dalla nave rovesciata e si affida alle onde, che lo portano indietro sulla riva.
Riunitosi con Elena, quest'ultima lo informa che Sully è stato catturato dagli uomini di Marlowe e portato su un convoglio nel deserto del Rub 'al-Khali, e possono salvarlo solo approfittando di un aereo cargo che sta trasportando provviste nel deserto. I due si infiltrano nella base aerea; lì, Nate chiede ad Elena di non continuare con lui per timore che sia un'impresa troppo pericolosa per lei, e la ragazza, abbattuta e contrariata, si allontana con una jeep. Tuttavia, sarà proprio il ritorno della ragazza che, con il veicolo, permetterà a Nate di salire in tempo sull'aereo già in corsa. Purtroppo Nathan viene scoperto dagli uomini di Marlowe, a cui segue uno scontro a fuoco con un conseguente grave danno all'aereo, facendolo precipitare: fortunatamente Nate riesce a salvarsi. Dopo aver vagato nel deserto per un paio di giorni, soffrendo di esaurimento da calore, sete e gravi allucinazioni, Nate arriva in una città fantasma, dove viene assalito dagli uomini di Marlowe, ma appare una truppa di beduini che lo salva scappando con i cavalli.
All'accampamento dei nomadi, Salim, il capo dei beduini, racconta a Nate che la città di Ubar era stata condannata migliaia di anni fa dal re Salomone quando questi imprigionò i malefici Djinn (demoni nati da fuoco purissimo) all'interno di un vaso in ottone, che gettò poi nel cuore della città. Egli accetta di aiutare Nate fermando gli inglesi e lo conduce al convoglio avvistato dai suoi compagni. Nate e Salim distruggono il convoglio e traggono in salvo Sully; una tempesta di sabbia però fa disperdere il gruppo, lasciando Sully e Nate insieme, dove si rendono conto di essere alle porte di Ubar.
Entrando a Ubar, Nate e Sully trovano una fontana d'acqua. Mentre Nate beve un sorso, Sully viene improvvisamente colpito e ucciso da Talbot. Sopraffatto dalla rabbia, Nate inizia ad inseguirlo e a combattere alcuni uomini di Marlowe, che sembrano essere stati posseduti dai Djinn, sputando fiamme e muovendosi a velocità elevata. Poco dopo averli uccisi, Nate trova Sully miracolosamente vivo. Il vero mistero di ciò che è accaduto al popolo di Ubar migliaia di anni prima diventa evidente: Nate aveva bevuto acqua contaminata in grado di provocare forti e reali allucinazioni. La contaminazione avvenne quando re Salomone buttò il vaso in ottone nelle profondità della città, dove si trova anche l'acquedotto. Il manufatto, a contatto con l'acqua, cominciò a contaminarla con un potente agente allucinogeno, portando così alla fine della civiltà all'interno di Ubar. Nathan, inoltre, intuisce che Francis Drake aveva trovato la città e fatto la loro stessa scoperta, ma, avendo compreso la sua pericolosità, se ne andò raccontando alla regina Elisabetta di non aver trovato nulla. Nate e Sully cercano e individuano Marlowe, che sta per recuperare il vaso di ottone dall'acqua, ma il duo provoca un'esplosione che innesca l'intero crollo della città. I due tentano la fuga, ma correndo incontrano Talbot e Marlowe; in quel momento il pavimento crolla e tutti, tranne Sally, cadono all'interno delle sabbie mobili. Nate e Talbot riescono ad uscire dalle sabbie mortali ma Marlowe no. Nate cerca di salvarla ma fallisce: Marlowe sprofonda nella sabbia con l'anello di Drake con sé. Il duo continua la fuga dalla città quando Nate viene attaccato da Talbot che cerca di ucciderlo, ma viene sconfitto anche grazie all'aiuto di Sully. Nate e Sully raggiungono l'entrata di Ubar e Salim corre in loro aiuto portandoli in salvo su dei cavalli, mentre la città viene inghiottita dal deserto.
Nate e Sully sono di ritorno verso l'aeroporto in Yemen, dove Sully espone al ragazzo un discorso serio, spiegandogli che viveva una vita molto solitaria e triste, migliorata solo dopo l'incontro con lo stesso Nate. Gli restituisce anche un particolare anello di Nate che teneva da molto tempo, raccomandandogli di fare le scelte giuste nella sua vita, e indicando Elena che, nel frattempo, li stava raggiungendo. Lei dice che forse lascerà lo Yemen e aggiunge che le dispiace molto per l'anello di Drake; Nate, come risposta, ribadisce che non è stata una grande perdita poiché lo ha scambiato con qualcosa di migliore, e ne approfitta per mostrarle l'anello che ha di nuovo indosso. I due, ormai riappacificati, si abbracciano e raggiungono Sully, che riserva loro una sorpresa: non viaggeranno in un normale aereo, bensì nel vecchio aeroplano simile a quello che Nate ed Elena avevano distrutto quattro anni prima alla ricerca di El Dorado, in memoria dei vecchi tempi.","PlayStation 3","Avventura dinamica");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Uncharted 4: Fine di un Ladro","2016-05-10","Naughty Dog","imgs/locandina Uncharted 4.jpg","","Il gioco inizia con Nathan 'Nate' Drake che, nel bel mezzo di una tempesta, sta guidando una barca inseguito da mercenari che tentano di uccidere lui e Samuel 'Sam' Drake, fratello di Nate, che si trova sulla barca con lui. Il duo sta cercando di raggiungere un'isola ma all'improvviso una nave in fiamme dei mercenari colpisce la barca e Nate e Sam finiscono per essere travolti in mare.
Poi inizia un flashback che mostra un giovane Nathan all'interno di un orfanotrofio, in custodia lì dalla morte della madre, le cui uniche consolazioni erano le visite segrete del fratello maggiore Sam, oramai maggiorenne e libero dai vicoli. Una notte, Sam mostrò a Nate una moto, comprata con i soldi guadagnati al lavoro, e gli disse che aveva in serbo per lui un'altra sorpresa.
Inizia poi un altro flashback, i cui eventi avvengono otto anni prima della scoperta di El Dorado, che raffigura un Nate adulto rinchiuso in una prigione di Panama assieme a Sam e il loro socio Rafe Adler, i quali sono entrati nel posto corrompendo la guardia Vargas per seguire le tracce di Henry Avery, il pirata che fece il più grande colpo della storia rubando carichi di oro e gioielli del valore di 400 milioni di dollari. Dopo aver ottenuto indizi e aver ucciso Vargas (anche questo non era secondo i piani, poiché fu un'azione imprevedibile di Rafe), il trio cercò di evadere dalla prigione ma durante la fuga Sam fu colpito da uno sparo e Nate, credendolo morto, scappò con Rafe, continuando la ricerca del tesoro anche se poi decise di lasciar perdere e abbandonò Rafe.
Tornando al presente, sono passati tre anni dalla scoperta di Iram dei Pilastri e Nate si è lasciato alle spalle il mondo dei cacciatori di tesori, condivide la vita matrimoniale con Elena Fisher e lavora come recuperatore di rifiuti urbani sommersi, trascorrendo così uno stile di vita più sedentario. Eppure le circostanze vogliono che egli ritorni nel giro a causa dell'improvvisa ricomparsa del fratello Sam.
Rivelatosi al fratello vivo, Sam racconta che era stato salvato e rinchiuso nella stessa prigione, avendo come compagno di cella il boss della droga Hector Alcazar. Gli scagnozzi del boss fecero evadere entrambi, ma Alcazar desiderava una parte del tesoro di cui Sam gli parlava sempre e gli diede tre mesi per trovarlo, altrimenti l'avrebbe ucciso.
Sam necessita quindi dell'aiuto di Nathan per impossessarsene, e il fratello è costretto ad accettare per salvargli la vita nonostante avesse promesso a Elena di farla finita con le avventure. Dopo aver mentito alla moglie adducendo un lavoro in Malesia, nuovamente insieme con Victor Sullivan, partono per la costiera amalfitana in Italia, dove partecipano a un'asta del mercato nero, occasione per rubare l'autentica croce di San Disma appartenuta ad Avery, contenente dei possibili indizi sull'ubicazione del tesoro. Anche lo stesso Rafe, che non ha mai smesso di cercare il tesoro, partecipa all'asta per lo stesso oggetto, in società con Nadine Ross, mercenaria a capo della Shoreline. Nate, Sam e Sully riescono a scappare portandosi dietro il manufatto rubato. Una mappa all'interno della croce li porta ad andare in Scozia, dove si trova la tomba del pirata.
Arrivati, riescono a trovare l'indizio successivo che li spinge a dirigersi a King's Bay, in Madagascar, in procinto di scoprire dove si trovi Libertalia, una leggendaria colonia pirata fondata da tutti i maggiori capitani per riunire tutti i bottini e convivere utopicamente in pace.
Dopo aver scoperto l'ubicazione esatta dell'isola attraverso una serie di prove nascoste in alcune torri sulla costa del paese, Nate viene scoperto da Elena. Nonostante la vicenda di Sam che le viene rivelata, la ragazza è delusa dal fatto che il marito non abbia avuto il coraggio di affrontarla dicendole la verità e che le abbia mentito. Quando gli volta le spalle per andarsene, Nate non la rincorre suscitando anche la delusione di Sully, che decide di abbandonarlo per raggiungere Elena.
Così rimangono Nate e Sam, che raggiungono l'isola e scoprono Libertalia: si presenta come una vera e propria città con quartieri commerciali e di lusso ormai decadenti. I fratelli arrivano, infine, a quella che doveva essere la banca della città e vi trovano cadaveri e barricate, segno che ci fu una guerra civile tra coloni e Fondatori. Una volta entrati nel palazzo, trovano solo casseforti vuote e i quadri dei Fondatori con la scritta LADRO: i pirati avevano deciso di tenere per loro la maggior parte delle ricchezze, imponendo tasse altissime ai cittadini, cosa che portò a una rivolta contro i loro signori, i quali scapparono in un'altra zona dell'isola: New Devon.
Ma durante la strada per New Devon, i fratelli Drake incontrano nemici: arriva Rafe, che rivela che è stato lui stesso ad aver fatto uscire Sam dalla galera due anni prima, per farsi aiutare nelle ricerche della tomba di Avery (essendo Alcazar morto sei mesi prima), ma Sam lo aveva tradito fuggendo con gli indizi. Deciso a riprendere Sam dalla sua parte, Rafe spara verso Nate, ma il fratello gli fa da scudo e viene colpito al braccio, e il rinculo fa accidentalmente cadere Nate in un dirupo. Durante la caduta, Nathan sbatte la testa, cade in acqua e perde i sensi.
Risvegliatosi tra le braccia di Elena, tornata per salvarlo (sebbene abbastanza recalcitrante all’idea), Nate le racconta il suo passato: continua così il flashback precedente, che mostra Sam che rivelò a Nate di aver scoperto l'ubicazione degli oggetti appartenuti alla madre e lo portò con la moto a destinazione per scoprire il loro passato insieme. I ragazzi trovarono il quaderno della madre all'interno di una vecchia villa, ma furono scoperti dalla proprietaria, un'anziana signora, che scoprendo la loro identità, rivelò ai ragazzi il passato di cercatrice di tesori della madre, in particolare la sua intenzione di trovare il tesoro di Avery. Tuttavia, la donna morì per un attacco al cuore, costringendo i ragazzi a fuggire poiché la donna aveva chiamato la polizia. Dopo questo fatto, i ragazzi sono ricercati, e Sam propone a Nate di continuare le ricerche della madre cambiando il cognome da Morgan a Drake, usando la teoria della madre secondo la quale Sir Francis Drake avesse avuto degli eredi.
Nate ed Elena decidono di seguire le tracce della Shoreline per salvare Sam e andarsene dall'isola. Arrivano a New Devon, alla villa del braccio destro di Avery, Thomas Tew, dove scoprono che insieme ad Avery, durante un brindisi, avvelenarono tutti gli altri dieci pirati Fondatori di Libertalia, tenendosi il tesoro per sé stessi. Passando nella villa dello stesso Avery, la coppia scopre che il tesoro fu trasportato dalla casa del pirata sulla sua nave, attraverso i sotterranei della villa, pieni di esplosivi per eludere eventuali inseguitori.
Una volta trovato Sam, egli si rifiuta di seguire Nate, Elena e Sully verso l'aereo perché deciso a continuare la ricerca del tesoro, che ha scoperto trovarsi sul galeone di Avery del quale conosce perfettamente la posizione. Così Nate è costretto a inseguirlo nuovamente per salvarlo. Nei pressi della nave, Nadine affronta Rafe che insiste a prendere il tesoro, nonostante sappia che la nave sia piena di esplosivi piazzati da Avery, rivelatosi paranoico e sociopatico nei suoi ultimi anni.
Nate arriva nella stanza del tesoro ormai in fiamme, e trova gli scheletri di Avery e Tew, che si erano uccisi a vicenda. Nadine tradisce Rafe, lasciando lui, Sam e Nate a morire. Dopo un rocambolesco scontro di spade, dove Rafe rivela che è stato sempre geloso di tutte le scoperte passate di Nate e che questa avrebbe dovuto essere la sua, muore schiacciato da una rete penzolante piena di tesori. I fratelli riescono a fuggire entrambi dal galeone in fiamme, ricongiungendosi con Sully ed Elena. Sam rivela a Nate che in realtà non è ancora soddisfatto e il fratello capisce ciò che prova: è lo stesso sentimento che lo aveva spinto a partire sempre per nuove avventure e lo aveva costretto a un'inconscia infelicità nella vita normale con Elena.
Tuttavia, ha ormai capito quali cose siano importanti da mantenere e quali da lasciar andare; Sam si augura lo stesso, ma intanto decide di mettersi in società con Sully, il quale può affidargli qualche lavoro adrenalinico. Tornato a casa, Nate scopre che Jameson, il suo capo, ha venduto l'azienda e che il proprietario è lo stesso Nate: Elena, infatti, con il ricavato ottenuto da alcune monete che Sam aveva rubato dal galeone di Avery, ha comprato l'azienda con lo scopo di trasformare l'attività in una più avventurosa, sempre alla ricerca di relitti, ma stavolta nella totale legalità, e insieme a una troupe televisiva per poter riaprire il suo vecchio show che aveva abbandonato dopo la scoperta di El Dorado per dedicarsi al lavoro di giornalista.
Molti anni dopo, Cassie Drake, la figlia di Nate ed Elena, ormai adolescente, trova per caso la chiave dell'armadio segreto dei genitori, dove sono contenuti cimeli e appunti di tutte le loro passate cacce al tesoro 'illegali'; dopo essere stati scoperti, Nate ed Elena decidono sia giunto il momento di raccontarle tutte le loro avventure. Il gioco si conclude con Elena che guarda la foto di lei, Nate e Sully tratta dal finale del primo capitolo, dove i tre sorridono soddisfatti con un fucile in mano e un tesoro alle spalle.","PlayStation 4","Avventura dinamica");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Watch Dogs","2014-05-27","Ubisoft","imgs/locandina WatchDogs 1.webp","","Nel 2012, Chicago diventa la prima città al mondo ad implementare il ctOS (central Operating System) - una rete informatica che collega ogni dispositivo in un unico sistema, sviluppato dalla società tecnologica Blume. Durante una rapina elettronica all'hotel di lusso Merlaut, l'hacker Aiden Pearce e il suo mentore e partner, Damien Brenks, fanno scattare un allarme silenzioso impostato da un altro hacker. Damien cerca di trovare l'hacker, consegnando se stesso e Aiden. Temendo per la sua famiglia, Aiden li porta al sicuro con la scusa di un viaggio a sorpresa in campagna. Sulla strada, il sicario Maurice Vega li attacca, causando un incidente d'auto che lascia la nipote di sei anni di Aiden, Lena, morta.
Un anno dopo, Aiden rintraccia Vega in uno stadio di baseball, ma non riesce a scoprire l'identità del suo sicario. Lasciando Vega nelle mani del suo partner, il faccendiere Jordi Chin, Aiden va a trovare sua sorella Nicole e suo nipote Jackson per il compleanno di quest'ultimo, ma scopre che qualcuno li sta molestando. Con l'aiuto di Clara Lille, un membro del gruppo hacker DedSec che sta cercando di smascherare la corruzione della Blume, Aiden rintraccia il molestatore, che si rivela essere Damien, il quale voleva attirare l'attenzione di Aiden in modo che lo aiutasse a trovare l'altro hacker del lavoro al Merlaut. Aiden rifiuta ma, mentre è impegnato a trattare con un testimone dello stadio, viene a sapere che Damien ha rapito Nicole per costringerlo a collaborare. Dopo aver allestito un nuovo nascondiglio nel Bunker - una ex base Blume non rilevabile con accesso al ctOS - Aiden rintraccia l'hacker con l'aiuto di Clara: il capo della banda dei Black Viceroys e veterano dell'esercito Delford 'Iraq' Wade. Per raggiungere i server di Iraq, Aiden si infiltra in un'asta di esseri umani a cui egli sta partecipando per copiare la sua chiave di accesso e ricatta suo cugino Tyrone 'Bedbug' Hayes per essere il suo uomo all'interno. Bedbug riesce a ottenere solo un campione di dati che rivela che Iraq ha informazioni su quasi tutti i cittadini di Chicago, proteggendo la sua banda dalle autorità tramite ricatto.
Quando Aiden e Clara si imbattono in dati criptati al di là delle loro capacità, rintracciano il leggendario hacker ed ex informatore della Blume Raymond 'T-Bone' Kenney, che ha causato il blackout del Nord-Est del 2003 mentre cercava di esporre i pericoli del ctOS, che aveva contribuito a creare. Mentre Aiden si infiltra nel quartier generale della Blume per cancellare l'identità di Kenney dal ctOS, permettendogli di tornare a Chicago, Damien pubblica la posizione di Kenney in cambio del pieno accesso al ctOS, costringendo Aiden a salvarlo prima che venga ucciso dalle forze di sicurezza private della Blume. In seguito, Aiden assalta il complesso di Iraq per finire di scaricare i dati del suo server, uccidendo Iraq quando lo affronta.
Aiden, Kenney e Clara non sono in grado di decifrare i dati perché un altro hacker, JB 'Defalt' Markowicz, si infiltra nel loro sistema, li ruba e li cancella dai loro server. Defalt rivela anche che Clara è stata assunta per rintracciare Aiden dopo il lavoro al Merlaut, quindi è indirettamente responsabile per la morte di Lena, il che porta Aiden a licenziarla con rabbia. Nel frattempo, infastidito dalla mancanza di progressi di Aiden, Damien rivela la sua identità alle autorità. Dopo aver recuperato i dati da Defalt, Kenney aiuta Aiden a rintracciare Nicole, permettendogli di salvarla e portare lei e Jackson fuori da Chicago per la loro sicurezza.
Mentre Kenney finisce di decifrare i dati, egli informa Aiden di chi ha ordinato l'omicidio: Il boss della mafia irlandese e proprietario del Merlaut, Dermot 'Lucky' Quinn. Aiden affronta Quinn, che rivela di aver ordinato il colpo perché credeva che Aiden stesse cercando un video segreto in cui il sindaco Donovan Rushmore uccide la sua segretaria dopo che lei viene a sapere dei suoi affari con Quinn, che quest'ultimo aveva usato per ricattare Rushmore. Dopo aver ucciso Quinn hackerando il suo pacemaker, Aiden viene informato da Damien che Quinn ha mandato dei sicari a cercare Clara perché è un peso. Incapace di salvarla, Aiden rende pubblico tutto il materiale ricattatorio, facendo infuriare Damien, che inizia a creare scompiglio a Chicago usando il ctOS. Aiden spegne il sistema usando un virus creato da Kenney e rintraccia Damien in un faro. Qui arriva anche Jordi, che è stato assunto per uccidere entrambi gli uomini, ma Aiden lo ferisce e uccide Damien. Più tardi, Jordi chiama Aiden un'ultima volta per dirgli dove è tenuto Vega; Aiden si dirige lì, e ne decide il destino.","PlayStation 3 | PlayStation 4 | XBox 360 | XBox One | PC","Avventura dinamica | Stealth");
INSERT INTO Videogioco(titolo,rilascio,casaProduttrice,imgLocandina,imgBanner,trama,piattaformaV,genereV) VALUES("Watch Dogs 2","2016-11-15","Ubisoft","imgs/locandina WatchDogs 2.jpg","","Nel 2016, tre anni dopo gli eventi del titolo precedente, San Francisco diventa la prima città a installare una versione migliorata del ctOS, una rete che collega ogni dispositivo elettronico in un singolo sistema, sviluppata dalla compagnia Blume. L'hacker Marcus Holloway, che in passato era stato incarcerato da questo 'ctOS 2.0' per un crimine da lui non commesso, decide di unirsi al gruppo di hacker DedSec (composto dai giovani hacker Sitara Dhawana, Josh Sauchak, Horatio Carlin e 'Wrench') e intraprende una sorta di iniziazione hackerando una server farm della Blume e cancellando il proprio profilo dal sistema. Il DedSec determina quindi che il nuovo ctOS può danneggiare i cittadini innocenti e decide di svelare la verità attraverso una campagna sui vari social media per reclutare numerosi follower, esponendo la corruzione e i crimini condotti attraverso il sistema, così da arrivare infine alla Blume.
Dopo aver svelato condotto diverse operazioni con successo (una contro il film CyberDriver, una contro la chiesa di New Dawn e l'ultima contro l'azienda produttrice di elettrodomestici Haum), Josh scopre delle irregolarità nel loro numero di follower, il cui numero è stato inspiegabilmente aumentato da account inattivi provenienti dalla sede del social media Invite. Quando Marcus va a indagare, si imbatte nel CTO della Blume Dušan Nemec, che ha sfruttato le recenti del DedSec per vendere il ctOS a possibili compratori timorosi di essere hackerati. Obbligato ad abbandonare temporaneamente la città per far calmare le acque, il DedSec partecipa al festival annuale dello Swelter Skelter e lì incontra il leggendario hacker Raymond 'T-Bone' Kenney. Incuriosito dalle sue azioni, Kenney si unisce al gruppo nella sua lotta contro la Blume. Marcus intraprende inoltre altre operazioni, vanificando gli sforzi di Dušan di dividere il DedSec (prima utilizzando il gruppo di hacker rivale Prime_Eight e poi servendosi persino dell'FBI) e vendicandosi della banda messicana dei Tezca, che nel frattempo ha ucciso Horatio dopo non essere riuscita a convincerlo a unirsi a loro. Infine, hackerando un satellite lanciato dalla compagnia aerospaziale Galilei, il gruppo svela ulteriori episodi di corruzione (il primo legato al senatore Mark Thruss, il secondo alla corporazione Tidis e il terzo agli Auntie Shu Boys, un'organizzazione criminale cinese segretamente alleata della Blume).
Il DedSec raccoglie infine informazioni sul programma di manipolazione di dati noto come 'Bellwether', programmato da Kenney, e scopre che usufruendo di questo e di una rete di satelliti volta a bypassare i cavi sottomarini, Dušan intende manipolare la politica e le finanze mondiali, monopolizzando i dati di tutto il mondo. Per fermarlo, il gruppo di hacker lancia un'enorme operazione che permette a Marcus di infiltrarsi nel quartier generale della Blume di San Francisco, hackerandone i server e inviando le informazioni alla polizia. Mentre Dušan viene arrestato e la Blume messa sotto indagine, il DedSec lancia un messaggio pubblico dove giura di continuare la sua lotta contro la Blume.","PlayStation 4 | XBox One | PC","Avventura dinamica | Stealth");